function animate() {

                let startX = directionalLight.position.x;
                let startY = directionalLight.position.y;
                var x = startX;
                var y = startY;

                let highY=0;

                if (animateLight){
                    yScalar = scale(directionalLight.position.y<0?0:directionalLight.position.y,0,dim*2.25,10,70);
                    directionalLight.position.x = x * Math.cos(lightRotationSpeed) + y * Math.sin(lightRotationSpeed);
                    directionalLight.position.y = y * Math.cos(lightRotationSpeed) - x * Math.sin(lightRotationSpeed);
                    if (directionalLight.position.y>highY){highY=directionalLight.position.y}
                    scene.background = new THREE.Color(hslToHex(200,100,yScalar));
                }




                requestAnimationFrame( animate );

                renderer.render( scene, camera );
            }

            animate();

