                if (!isMobile){
                light1.shadow.map.dispose();
                directionalLight.shadow.map.dispose();
                if (penType=="hallway"){spotLight.shadow.map.dispose();}}

            }

            function orbitSpeedUp(){

                    if (lightRotationSpeed<0.05){
                        lightRotationSpeed+=0.0001;
                    } 

                }
            function orbitSpeedDown(){

                    if (lightRotationSpeed>0){
                        lightRotationSpeed-=0.0001;
                    } else {
                        lightRotationSpeed=0;
                    }

            }


            function hslToHex(h, s, l) {
                l /= 100;
                const a = s * Math.min(l, 1 - l) / 100;
                const f = n => {
                    const k = (n + h / 30) % 12;
                    const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
                    return Math.round(255 * color).toString(16).padStart(2, '0');  
                };
                return `#${f(0)}${f(8)}${f(4)}`;
            }

            function rads(degrees){
                var pi = Math.PI;
                return degrees * (pi/180);
            }

            function scale (number, inMin, inMax, outMin, outMax) {
                return (number - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
            }

