                            const mullionGeoV = new THREE.ExtrudeGeometry (mullionV, extrudeSettingsMullion);
                            let mullionMaterialV = new THREE.Mesh(
                                mullionGeoV,
                                black
                            );
                            mullionMaterialV.position.z=wallThickness/2-mullionThickness/2;
                            mullionMaterialV.castShadow=true;
                            mullionMaterialV.receiveShadow=true;
                            wallGroup.add(mullionMaterialV);

                        }

                        for (let i=0;i<hMullions+2;i++){
                            let mullionH = new THREE.Shape();

                            mullionH.moveTo(
                                paddingA+paddingB+(p*(penWidth+penSpacing)),
                                yPos=="low"?(b+(dim/2-penHeight)/2)-(mullionWidth/2)+(i*hMullionSpacing):yPos=="high"?(b+dim/2+((dim/2-penHeight)/1.5))+(i*penHeight)-mullionWidth/2: yPos=="centerOnFace"? ((b+(dim/2+railHeight-penHeight))/2)-(mullionWidth/2)+(i*hMullionSpacing):(b+(dim-penHeight)/2)-(mullionWidth/2)+(i*hMullionSpacing)

                            );
                            mullionH.lineTo(
                                paddingA+paddingB+(p*(penWidth+penSpacing))+penWidth,
                                yPos=="low"?(b+(dim/2-penHeight)/2)-(mullionWidth/2)+(i*hMullionSpacing):yPos=="high"?(b+dim/2+((dim/2-penHeight)/1.5))+(i*penHeight)-mullionWidth/2: yPos=="centerOnFace"? ((b+(dim/2+railHeight-penHeight))/2)-(mullionWidth/2)+(i*hMullionSpacing):(b+(dim-penHeight)/2)-(mullionWidth/2)+(i*hMullionSpacing)

                            );
                            mullionH.lineTo(
                                paddingA+paddingB+(p*(penWidth+penSpacing))+penWidth,
                                yPos=="low"?(b+(dim/2-penHeight)/2)+(mullionWidth/2)+(i*hMullionSpacing):yPos=="high"?(b+dim/2+((dim/2-penHeight)/1.5))+(i*penHeight)+mullionWidth/2: yPos=="centerOnFace"? ((b+(dim/2+railHeight-penHeight))/2)+(mullionWidth/2)+(i*hMullionSpacing):(b+(dim-penHeight)/2)+(mullionWidth/2)+(i*hMullionSpacing)
                            );
                            mullionH.lineTo(
                                paddingA+paddingB+(p*(penWidth+penSpacing)),
                                yPos=="low"?(b+(dim/2-penHeight)/2)+(mullionWidth/2)+(i*hMullionSpacing):yPos=="high"?(b+dim/2+((dim/2-penHeight)/1.5))+(i*penHeight)+mullionWidth/2: yPos=="centerOnFace"? ((b+(dim/2+railHeight-penHeight))/2)+(mullionWidth/2)+(i*hMullionSpacing):(b+(dim-penHeight)/2)+(mullionWidth/2)+(i*hMullionSpacing)
                            )
                            const mullionGeoH = new THREE.ExtrudeGeometry (mullionH, extrudeSettingsMullion);
                            let mullionMaterialH = new THREE.Mesh(
                                mullionGeoH,
                                black
                            );
                            mullionMaterialH.position.z=wallThickness/2-mullionThickness/2;
                            mullionMaterialH.castShadow=true;
                            mullionMaterialH.receiveShadow=true;
                            wallGroup.add(mullionMaterialH);
                        }

                        if (penType=="breezeBlocks"){
                            for (let i=0;i<vMullions+1;i++){
                                for (let j=0;j<hMullions+1;j++){

                                    let block = createBlock();
                                    block.position.x = paddingA+paddingB+mullionWidth/8+(i*(0.04*dim)+(0.04*dim)/2);
                                    block.position.y = (b+(dim-penHeight)/2)+mullionWidth/8+(j*0.04*dim)+(0.04*dim)/2;
                                    block.position.z = wallThickness/2-blockThickness/2;

                                    wallGroup.add(block);
                                }
                            }
                        }
                    }
                }

            return wallGroup;
            }

function createBlock(){
                let color = bColor;

                let blockGroup = new THREE.Group();
                let blockDim = 0.0375 * dim;
                let borderThickness = 0.1*blockDim;

                let l = -blockDim/2;
                let r = blockDim/2;
                let t = blockDim/2;
                let b = -blockDim/2;

                let block = new THREE.Shape();

                block.moveTo(l,b);
                block.lineTo(l,t);
                block.lineTo(r,t);
                block.lineTo(r,b);

                let hole = new THREE.Shape();
                    hole.moveTo(l+borderThickness,b+borderThickness);
                    hole.lineTo(l+borderThickness,t-borderThickness);
                    hole.lineTo(r-borderThickness,t-borderThickness);
                    hole.lineTo(r-borderThickness,b+borderThickness);

                block.holes.push(hole);

                const extrudeSettings = {
                    depth: blockThickness,
                    bevelEnabled: false,
                };

                const blockGeo = new THREE.ExtrudeGeometry( block, extrudeSettings );
                var blockMesh = new THREE.Mesh(blockGeo,color);
                    blockMesh.material.side = THREE.DoubleSide;
                    blockMesh.receiveShadow = true;
                    blockMesh.castShadow = true;

                return blockMesh;
            }

            function createRailing(){
                let stairsLength = numStairs*stairDepth;
                var face = new THREE.Shape();

                    face.moveTo(0,railHeight);
                    if (stairsLanding){
                        face.lineTo(((numStairs-landingStep)*stairDepth)-stairDepth,(numStairs-landingStep)*stairHeight+railHeight);
                        face.lineTo(((numStairs-landingStep)*stairDepth)+stairWidth-stairDepth,(numStairs-landingStep)*stairHeight+railHeight);
                    }
                    face.lineTo(stairsLanding?stairsLength+stairWidth-stairDepth:stairsLength,plenTop+railHeight);
                    face.lineTo(stairsLanding?stairsLength+stairWidth-stairDepth:stairsLength,0);
                    face.lineTo(0,0);


                const extrudeSettings = {
                    depth: wallThickness,
                    bevelEnabled: false,
                };

                const faceGeo = new THREE.ExtrudeGeometry( face, extrudeSettings );
                var mesh = new THREE.Mesh(faceGeo,grey);
                    mesh.castShadow=true;
                    mesh.receiveShadow=true;
                    mesh.position.y=-dim/2;


                    mesh.position.z=stairsLanding?stairsLength+dim/5+(stairWidth-stairDepth):stairsLength+dim/5;



                    mesh.position.x=stairSide=="right"?(dim/5*1.5)-extrudeSettings.depth:-(dim/5*1.5);


                    mesh.rotation.y=rads(90);


                scene.add(mesh);   
            }
            function createBeam(){
                const beam = new THREE.Mesh(new THREE.BoxGeometry(dim,beamWidth, beamWidth), midGrey);
                beam.receiveShadow=true;
                beam.castShadow=true;
                beam.rotation.y=rads(90);
                beam.position.y=dim/2-beamWidth/2;
                beam.position.z=dim/2;
                beam.position.x=-dim/2+beamWidth/2;
                return beam;
            }
            function addBeams(){
                let beamGroup = new THREE.Group();
                let numBeams = Math.floor(penType=="hallway"?dim*3:dim*2/beamWidth/2);
                for (let i=0;i<numBeams;i++){
                    let beam = createBeam().clone();
                    beam.position.x=i*beamWidth*2;
                    beamGroup.add(beam);

                }
                beamGroup.position.x= -(dim/2)+beamWidth/2+(dim-((numBeams*beamWidth*2)-beamWidth))/2; 
                if (R.random_dec()<1){
                    beamGroup.rotation.y=rads(90);
                    beamGroup.position.x=-dim/2;
                    beamGroup.position.z=penType=="hallway"?dim*3-beamWidth/2:dim*2-beamWidth/2;
                }
                return beamGroup;
            }
            function createCourtyard(){

                let wallHeight = penType=="hallway"?dim/4:dim/1.7;

                let backWall = new THREE.Mesh(new THREE.BoxGeometry(dim,wallHeight,wallThickness),lColor);
                    backWall.position.y=-dim/2+wallHeight/2;
                    backWall.position.x=stairSide=="right"?-dim-wallThickness:dim+wallThickness;
                    backWall.position.z=wallThickness/2;
                    backWall.receiveShadow=true;
                    backWall.castShadow=true;
                scene.add(backWall);

                let sideWall = new THREE.Mesh(new THREE.BoxGeometry(wallThickness,wallHeight,penType=="hallway"?dim*3:dim*2),lColor);
                    sideWall.position.y=-dim/2+wallHeight/2;
                    sideWall.position.x=stairSide=="right"?-dim*1.5-wallThickness:dim*1.5+wallThickness;
                    sideWall.position.z=penType=="hallway"?dim*1.5:dim+wallThickness;
                    sideWall.receiveShadow=true;
                    sideWall.castShadow=true;
                scene.add(sideWall);
            }

function createStair(){

                let color = stairColor;

                let glassMaterial = new THREE.MeshPhysicalMaterial({
                        transparent: true,
                        transmission: 0.7,
                        roughness: 1,
                        opacity:1,
                        color:"aqua"
                    });


                const stair = new THREE.Mesh(new THREE.BoxGeometry(stairWidth,stairThickness,stairDepth),color);
                stair.castShadow=true;
                stair.receiveShadow=true;

                return stair;

            }

            function createBlockStaircaseStraight(){

                let b = 0;
                let t = dim/2;
                let l = -stairDepth*numStairs/2;
                let r = stairDepth*numStairs/2;
                console.log(landingStep);

                const extrudeSettings = {
                    depth: stairWidth,
                    bevelEnabled: false,
                };

                let profile = new THREE.Shape();

                profile.moveTo(l,b);
                profile.lineTo(l,t);

                for (let i=1;i<numStairs+1;i++){
                    profile.lineTo(l+(stairDepth*i),t-(stairHeight*(i-1)));
                    profile.lineTo(l+(stairDepth*i),t-(stairHeight*(i-1))-stairHeight);
                }

                let extrudedStairs = new THREE.ExtrudeGeometry(profile,extrudeSettings);
                let stairs = new THREE.Mesh(extrudedStairs,grey);
                stairs.receiveShadow=true;
                stairs.castShadow=true;
                stairs.rotation.y = rads(-90);
                stairs.position.z = (stairDepth*numStairs/2)+plenDepth;
                stairs.position.y = -dim/2;
                stairs.position.x=stairSide=="right"?dim/2:-dim/2+stairWidth;

                return stairs;
            }

            function createBlockStaircaseTurn(){

                let color=penType=="hallway"?midGrey:grey;

                let stairGroup = new THREE.Group();

                let b = 0;
                let t = dim/2;
                let l = -stairDepth*numStairs/2;
                let r = stairDepth*numStairs/2;
                console.log(landingStep);



                for (let i=0;i<numStairs;i++){
                    let stepTop = new THREE.Mesh(new THREE.PlaneGeometry(stairWidth,i==landingStep?stairWidth:stairDepth),color);
                    stepTop.castShadow=true;
                    stepTop.receiveShadow=true;
                    if (i<=landingStep){
                        stepTop.material.side=THREE.DoubleSide;
                        stepTop.rotation.x=rads(90);
                        stepTop.position.z=i==landingStep?plenDepth+stairWidth/2+((i)*(stairDepth)):plenDepth+stairDepth/2+((i)*(stairDepth));
                        stepTop.position.y=t-(stairHeight*i);
                    } else {
                        stepTop.position.z=plenDepth+(landingStep*stairDepth)-stairWidth/2+stairWidth;
                        stepTop.position.y=t-(stairHeight*i);
                        stepTop.position.x=stairSide=="right"?-stairWidth/2-stairDepth/2:stairWidth/2+stairDepth/2;
                        stepTop.translateX(stairSide=="right"?-(stairDepth*(i-landingStep-1)):stairDepth*(i-landingStep-1));
                        stepTop.rotation.x=rads(90);
                        stepTop.rotation.z=rads(90);
                    }
                    stairGroup.add(stepTop);

                    let stepFront = new THREE.Mesh(new THREE.PlaneGeometry(stairWidth, stairHeight),color);
                    stepFront.castShadow=true;
                    stepFront.receiveShadow=true;
                    if (i<landingStep){
                        stepFront.position.z=i==landingStep?plenDepth+stairWidth+(stairDepth*i):plenDepth+stairDepth+(stairDepth*i);

                            stepFront.position.y = t-(stairHeight/2)-(stairHeight*i);

                    } else {
                        stepFront.position.z=plenDepth+stairWidth/2+(landingStep*stairDepth);
                        stepFront.position.x=stairSide=="right"?-stairWidth/2:stairWidth/2;
                        stepFront.translateX(stairSide=="right"?-(stairDepth*(i-landingStep-1))-stairDepth:stairDepth*(i-landingStep-1)+stairDepth);
                        stepFront.position.y=t-(stairHeight*i)-(stairHeight/2);
                        stepFront.rotation.y=rads(90);
                    }
                    stairGroup.add(stepFront);
                }

                let profileA = new THREE.Shape();
                    profileA.moveTo(l,b);
                    profileA.lineTo(l,t);

                for (let i=0;i<=landingStep;i++){
                    profileA.lineTo(l+(stairDepth*i),t-(stairHeight*(i-1)));
                    profileA.lineTo(l+(stairDepth*i),t-(stairHeight*(i-1))-stairHeight);
                }
                    profileA.lineTo(l+stairDepth*landingStep,b);

                let profileAGeo = new THREE.ShapeGeometry(profileA);
                let profileAMesh = new THREE.Mesh(profileAGeo,color);
                profileAMesh.castShadow=true;
                profileAMesh.receiveShadow=true;
                profileAMesh.position.z=(stairDepth*numStairs/2)+plenDepth;
                profileAMesh.translateX(stairSide=="right"?-stairWidth/2:stairWidth/2);
                profileAMesh.rotation.y=rads(-90);
                stairGroup.add(profileAMesh);

                let l2 = 0;

                let profileB = new THREE.Shape();
                    profileB.moveTo(l2,b);
                    profileB.lineTo(l2,b+(numStairs-landingStep)*stairHeight);


                for (let i=0;i<(numStairs-landingStep);i++){
                    profileB.lineTo(l2+stairWidth+(stairDepth*i),(b+(numStairs-landingStep)*stairHeight-(stairHeight*i)))
                    profileB.lineTo(l2+stairWidth+(stairDepth*i),(b+(numStairs-landingStep)*stairHeight-(stairHeight*i))-stairHeight);
                }

                let profileBWidth = stairWidth+(stairDepth*4);


                let profileBGeo = new THREE.ShapeGeometry(profileB);
                let profileBMeshA = new THREE.Mesh(profileBGeo,color);

                profileBMeshA.castShadow=true;
                profileBMeshA.receiveShadow=true;

                profileBMeshA.position.z=plenDepth+(stairDepth*landingStep)+stairWidth;
                profileBMeshA.rotation.y=stairSide=="right"?rads(180):0;

                profileBMeshA.position.x=stairSide=="right"?stairWidth/2:-stairWidth/2;

                stairGroup.add(profileBMeshA);

                let profileBMeshB = profileBMeshA.clone();

                profileBMeshB.translateZ(stairSide=="right"?stairWidth:-stairWidth);
                stairGroup.add(profileBMeshB);


                stairGroup.position.y = -dim/2;
                stairGroup.position.x=stairSide=="right"?dim/2-stairWidth/2:-dim/2+stairWidth/2;

                return stairGroup;
            }

            function createStaircase(){
                let stairGroup = new THREE.Group();
                for (let i=0;i<numStairs;i++){

                    let stair;

                    if (stairType=="z"){
                        stair = createZStair();
                    } else {
                        stair = createStair();
                    }



                    if (i==landingStep && stairsTurn){
                        stair.position.z=i*stairDepth+stairWidth/2-stairDepth/2;
                        stair.scale.set(1,1,stairWidth/stairDepth);

                    } else {
                        if (i>landingStep && stairsTurn){
                            stair.position.z=(landingStep-1)*stairDepth+stairWidth/2+stairDepth/2;
                            stair.position.x=stairSide=="right"?-(stairWidth/2+stairDepth/2+((i-landingStep-1)*stairDepth)):stairWidth/2+stairDepth/2+((i-landingStep-1)*stairDepth);
                            stair.rotation.y=rads(90);

                        } else {
                            stair.position.z=i*stairDepth;

                        }

                    }

                    stair.position.y=-i*stairHeight;

                    stairGroup.add(stair);

                }
                stairGroup.position.z=stairType=="z"?plenDepth+(0.5*stairDepth)-stairDepth/2:plenDepth+(0.5*stairDepth);
                stairGroup.position.x=stairSide=="left"? -dim/2.5 : dim/2.5;
                stairGroup.position.y=stairType=="z"?0:-stairThickness/2;
                return stairGroup;
            }

            function createLadder(){
                let stairGroup = new THREE.Group();

                for (let i=0;i<numStairs;i++){

                    let stair = createStair();

                    stair.position.y=-i*stairHeight;

                    stairGroup.add(stair);

                }

                stairGroup.position.z=plenDepth+(0.5*stairDepth);
                stairGroup.position.x=stairSide=="left"? -dim/2.5 : dim/2.5;
                stairGroup.position.y=-stairThickness/2;

                return stairGroup;
            }

            function createZStair(){

                let color = grey;


                const extrudeSettings = {
                    depth: stairWidth,
                    bevelEnabled: false,
                };

                var face = new THREE.Shape();
                    face.moveTo(stairDepth,0);
                    face.lineTo(stairDepth,-stairHeight-stairThickness);
                    face.lineTo(stairDepth-stairThickness,-stairHeight-stairThickness);
                    face.lineTo(stairDepth-stairThickness,-stairThickness);
                    face.lineTo(0,-stairThickness);
                    face.lineTo(0,0);
                const profile = new THREE.ExtrudeGeometry(face, extrudeSettings);

                const stair = new THREE.Mesh(profile,color);
                stair.castShadow=true;
                stair.receiveShadow=true;

                stair.position.x = stairWidth/2;
                stair.rotation.y=rads(-90);

                return stair;

            }

function createRoomStandard(){
               let room = new THREE.Group();

               const ceilPlane = new THREE.Mesh(new THREE.BoxGeometry(dim,dim*2,wallThickness), white);
               ceilPlane.position.y = dim/2+wallThickness/2;
               ceilPlane.rotation.x= rads(90);
               ceilPlane.position.z = dim;
               ceilPlane.castShadow=true;
               ceilPlane.receiveShadow=true;
               room.add(ceilPlane);

               return room;
           }

           function createRoomHallway(){
               let room = new THREE.Group();

               const lowCeilPlane = new THREE.Mesh(new THREE.BoxGeometry(dim,dim*2,wallThickness), white);
               lowCeilPlane.position.y = wallThickness/2;
               lowCeilPlane.rotation.x= rads(90);
               lowCeilPlane.position.z = dim*2;
               lowCeilPlane.castShadow=true;
               lowCeilPlane.receiveShadow=true;
               room.add(lowCeilPlane);

               const ceilPlane = new THREE.Mesh(new THREE.BoxGeometry(dim,dim*3,wallThickness), white);
               ceilPlane.position.y = dim/2+wallThickness/2;
               ceilPlane.rotation.x= rads(90);
               ceilPlane.position.z = dim*1.5;
               ceilPlane.castShadow=true;
               ceilPlane.receiveShadow=true;
               room.add(ceilPlane);

               const sideWall = new THREE.Mesh(new THREE.BoxGeometry(dim/2,dim*1.5,wallThickness), white);
               sideWall.position.y = -dim/4;
               sideWall.rotation.y= rads(90);
               sideWall.rotation.z= rads(90);
               sideWall.position.z = dim*2.25;
               sideWall.castShadow=true;
               sideWall.receiveShadow=true;
               room.add(sideWall);

               return room;
           }

function createPlenum(){

                let color=grey;
                let plenum = new THREE.Group();

                const plenumTop = new THREE.Mesh (new THREE.PlaneGeometry(dim,dim/5.02), stairColor);
                    plenumTop.material.side = THREE.DoubleSide;
                    plenumTop.rotation.x = rads(90);
                    plenumTop.position.z = dim/10;
                    plenumTop.receiveShadow=true;
                    plenumTop.castShadow=true;
                plenum.add(plenumTop);

                const plenumFace = new THREE.Mesh (new THREE.PlaneGeometry(wallThickness,stairWidth), color);
                    plenumFace.material.side = THREE.DoubleSide;
                    plenumFace.rotation.z = rads(90);
                    plenumFace.position.z = dim/5;
                    plenumFace.position.y = -wallThickness/2;
                    plenumFace.position.x = stairSide=="left"?-2*dim/5:2*dim/5;
                    plenumFace.receiveShadow=true;
                    plenumFace.castShadow=true;
                plenum.add(plenumFace);

                const plenumBottom = new THREE.Mesh (new THREE.PlaneGeometry(dim,dim/5), color);
                    plenumBottom.material.side = THREE.DoubleSide;
                    plenumBottom.rotation.x = rads(90);
                    plenumBottom.position.z = dim/10;
                    plenumBottom.position.y = -wallThickness;
                    plenumBottom.receiveShadow = true;
                    plenumBottom.castShadow = true;
                plenum.add(plenumBottom);

                var face = new THREE.Shape();
                    face.moveTo(dim,0);
                    face.lineTo(dim,plenTop+railHeight);
                    face.lineTo(dim/5,plenTop+railHeight);
                    face.lineTo(dim/5,dim/2-wallThickness);
                    face.lineTo(0,dim/2-wallThickness);
                    face.lineTo(0,0);

                const extrudeSettings = {
                    depth: wallThickness,
                    bevelEnabled: false,
                };

                const faceGeo = new THREE.ExtrudeGeometry( face, extrudeSettings );

                var mesh = new THREE.Mesh(faceGeo,color );
                    mesh.position.z=stairSide=="left"?dim/5-wallThickness:dim/5;
                    mesh.position.y=-dim/2;
                    mesh.position.x=stairSide=="left"?-dim/2:dim/2;
                    mesh.rotation.y=stairSide=="left"?0:rads(180);
                    mesh.receiveShadow=true;
                    mesh.castShadow=true;
                    mesh.material.side = THREE.DoubleSide;
                plenum.add(mesh);

                const backWall = new THREE.Mesh(new THREE.PlaneGeometry(dim,dim), ltGrey);
                    backWall.material.side = THREE.DoubleSide;
                    backWall.receiveShadow=true;
                    backWall.castShadow=true;
                plenum.add(backWall);

                return plenum;
            }
            function createTileFloor(){

            let tilesGroup = new THREE.Group();
            let tilesW = Math.ceil(R.random_dec()*(15-5)+5);
            let tilesD = Math.ceil(R.random_dec()*(30-10)+10);
            let groutJoint = 0.5/500*dim;
            let tileW = (dim*2+(wallThickness*2)-(tilesW*groutJoint))/tilesW;
            let tileD = (dim*3-(tilesD*groutJoint))/tilesD;


            for (let i=0;i<tilesW;i++){
                for (let j=0;j<tilesD;j++){
                    let tile = new THREE.Mesh(new THREE.BoxGeometry(tileW,wallThickness/5,tileD),new THREE.MeshPhongMaterial({color:hslToHex(0, 0, R.random_dec()*(colorRange-(colorRange-colorVariation))+colorRange-colorVariation)}));
                    tile.receiveShadow=true;
                    tile.castShadow=true;
                    tile.position.x = i * (tileW+(groutJoint));
                    tile.position.z = j * (tileD+(groutJoint));
                    tilesGroup.add(tile);
                }
            }
            tilesGroup.position.y=-(dim/2+wallThickness/15);
            tilesGroup.position.x=stairSide=="left"?-(dim/2-tileW/2)-wallThickness:-(dim/2-tileW/2)-wallThickness-dim;
            tilesGroup.position.z=tileD/2;
            scene.add(tilesGroup);

            }

if (isMobile){
              drawArt();
              setCameraPosition();
              scene.add(aLight);
              zoomIntoArt();
          } else {
          drawSpace();
          }

          function drawSpace(){

              let stairs;

              switch(stairType){

                  case "block":
                      stairs = createBlockStaircaseStraight();
                      break;

                  case "blockturn":
                      stairs = createBlockStaircaseTurn();
                      break;

                  case "floating":
                      stairs = createStaircase();
                      break;

                  case "floatingturn":
                      stairs = createStaircase();
                      break;

                  case "ladder":
                      stairs = createLadder();
                      break;

                  case "z":
                      stairs = createStaircase();
                      }

              scene.add(stairs);

              drawArt();

              let sideWallR = createWall(stairSide=="right"?white:pColor,stairSide=="right"?false:true);
                  sideWallR.rotation.y=rads(-90);
                  sideWallR.position.x=dim/2+wallThickness;
                  sideWallR.position.y=-dim/2;
              scene.add(sideWallR);

              let sideWallL = createWall(stairSide=="left"?white:pColor,stairSide=="left"?false:true);
                  sideWallL.rotation.y=rads(-90);
                  sideWallL.position.x=-dim/2;
                  sideWallL.position.y=-dim/2;
              scene.add(sideWallL);

              createTileFloor();
              let plenum = createPlenum();
                  scene.add(plenum);
              let room = penType=="hallway"?createRoomHallway():createRoomStandard();
                  scene.add(room);
              if (railing && !stairsLanding){
                  createRailing();
              }
              if (beams && penType!="breezeBlocks" && penType!="hallway"){
                  scene.add(addBeams());
              }

              if (penType != "hallway"){
                  createCourtyard();
              }

              addLights();
