function drawArt(){

            let rotate = Math.floor(R.random_dec()*(2-0)+0);
            let splitThickness = Math.floor(R.random_dec()*(3-2)+2);


            let splitPoint = Math.floor(R.random_dec()*((artDim*0.9)-(artDim*0.1))+(artDim*0.1));
            let extentA = splitPoint+(Math.floor(splitThickness/300*dim));
            let extentB = splitPoint-(Math.floor(splitThickness/300*dim));
            let extentDiff = extentA-extentB;
            let strokeWidth = Math.floor(1/500*dim);

            let gradSelect = Math.floor(R.random_dec()*2);

            let spread = R.random_dec()*((10/300*artDim)-(5/300*artDim))+(5/300*artDim); 

            let splitDifference = Math.floor(R.random_dec()*(30-20)+20);

            let materialArray = new Array(artDim);

            let painting = new THREE.Mesh(
                new THREE.BoxGeometry(artDim,artDim,10/500*dim,artDim,1,1),
                materialArray);

                let padding = painting.geometry.faces.length/2;


            for (let i=0; i<artDim+1; i++){
                if (gradSelect===0?i<splitPoint-(Math.floor(splitThickness/300*dim)):i>splitPoint+(Math.floor(splitThickness/300*dim))){
                    let fillColor = (((gradSelect===0?i:(Math.floor(360/1000*dim))-i)/spread)+startColor)%360;
                    let color = new THREE.MeshPhongMaterial({color:hslToHex(fillColor, 100, 50), fog:false});
                    materialArray[i]=color;
                    painting.geometry.faces[i*2+padding].materialIndex=i;
                    painting.geometry.faces[i*2+1+padding].materialIndex=i;

                } else if (gradSelect===0?i>splitPoint+(Math.floor(splitThickness/300*dim)):i<splitPoint-(Math.floor(splitThickness/300*dim))){
                    let fillColor = (((gradSelect===0?splitPoint:(Math.floor(360/1000*dim))-splitPoint)/spread)+startColor+splitDifference)%360;
                    if (i==0){console.log(fillColor)};
                    let color = new THREE.MeshPhongMaterial({color:hslToHex(fillColor, 100, 50), fog:false});
                    materialArray[i]=color;
                    painting.geometry.faces[i*2+padding].materialIndex=i;
                    painting.geometry.faces[i*2+1+padding].materialIndex=i;

                } else if (i==splitPoint-extentDiff/2) {
                    let fillColor = (((gradSelect===0?splitPoint:(Math.floor(360/1000*dim))-splitPoint)/spread)+startColor+(splitDifference/2))%360;
                    let color = new THREE.MeshPhongMaterial({color:hslToHex(fillColor, 100, 50), fog:false});
                    materialArray[i]=color;

                    let fillColor2 = (((gradSelect===0?splitPoint:(Math.floor(360/1000*dim))-splitPoint)/spread)+startColor+(splitDifference/2)+3)%360;

                    let color2 = new THREE.MeshPhongMaterial({color:hslToHex(fillColor2, 100, 50), fog:false});
                    materialArray[i+1]=color2;

                    for (let j=i+strokeWidth;j<i+extentDiff+1-strokeWidth;j++){

                        painting.geometry.faces[j*2+padding].materialIndex=i;
                        painting.geometry.faces[j*2+1+padding].materialIndex=i;
                                }

                    for (let k=i;k<i+strokeWidth;k++){

                        painting.geometry.faces[k*2+padding].materialIndex=i+1;
                        painting.geometry.faces[k*2+padding+1].materialIndex=i+1;
                        painting.geometry.faces[k*2+(extentDiff-strokeWidth+1)*2+padding].materialIndex=i+1;
                        painting.geometry.faces[k*2+(extentDiff-strokeWidth+1)*2+padding+1].materialIndex=i+1;

                    }
                }
            }
            painting.position.set(0,-dim/2+artDim/2,dim/5+(5/500*dim));


            if (penType=="hallway"){
                painting.translateX(stairSide=="left"?artDim/2+(dim/2-artDim)/2:-artDim/2-(dim/2-artDim)/2);
            } else {
                if (railing){
                    painting.translateX(stairSide=="right"?(-(1/5*dim)/2)-wallThickness/2:((1/5*dim)/2)+wallThickness/2);  
                } else {
                    painting.translateX(stairSide=="right"?-(1/5*dim)/2:(1/5*dim)/2);
                }
            }

            painting.translateY(penType=="hallway"?(dim/2-artDim)/2:(dim/2+railHeight-artDim)/2);

            if (rotate){
                painting.rotation.z=rads(90);
            }

            painting.castShadow=true;
            painting.receiveShadow=true;
            scene.add(painting);   
