const headless = /HeadlessChrome/.test(window.navigator.userAgent);

let nodes = [];
let nodesToDraw = 1;
let nodeOrder = [];

// COLORS
let cred = [1, 0.8, 0.76];
let cblue = [210, 0.72, 0.7];
let cyellow = [46, 0.82, 0.95];
let cwhite = [0, 0, 0.96];
let cback = [cred,cblue,cyellow,cwhite];

// CHANCE PARAMS
let _cbackTop, _cbackBottom;
let _rotate;
let _weightFactor;
let _metaWonk;
let _mirrorChance;
let _xydivs;
let _mirror;
let _rwonk;

// FIXED PARAMS
let maxUnits = 17;
let minUnits = 9;
let minWonk = 0.001;
let maxWonk = 0.45;
let speedScalar = 0.2;

let maxDistance;
let xygap;
let bigdim;
let sameWeight;
let sameThinWeight;
let newBackground = true;

function setup() {
  createCanvas(window.innerWidth, window.innerHeight, WEBGL);
  pixelDensity();
  colorMode(HSB, 360, 1, 1, 1);
  r = new RND();
  defineNetwork(true);
  setAttributes('alpha', false);
}

function rollDice() {
  _cbackBottom = int(r.rb(0, cback.length));
  _cbackTop = int(r.rb(0, cback.length));
  _weightFactor = r.rb(0.066, 0.15);
  _metaWonk = r.rb(minWonk, maxWonk);
  _rotate = r.rb(0, TWO_PI);
  _mirrorChance = r.rb(0,1);
  _xydivs = int(r.rb(minUnits, maxUnits));
  _rwonk = r.rb(0,1);
}

function draw() {
  if (newBackground) {
    defineBackground();
    newBackground = false;
  }
  if (nodesToDraw < nodes.length-1 && frameCount % 10 == 0) {
    nodesToDraw++;
  }
  push();
  rotate(_rotate);
  for (let i = 0; i < nodesToDraw; i++) {
    nodes[i].displayNetwork();
  }
  pop();
}

function keyPressed() {
  if (key == ' ') {
    defineNetwork(false);
  }
}

function windowResized() {
  if (!headless) {
    resizeCanvas(window.innerWidth, window.innerHeight);
    r = new RND();
    defineNetwork(true);
  }
}

function defineBackground() {
  noStroke();
  beginShape();
  fill(cback[_cbackTop])
  vertex(-width/2, -height/2);
  vertex(width/2, -height/2);
  fill(cback[_cbackBottom]);
  vertex(width/2, height/2);
  vertex(-width/2, height/2);
  endShape();
}

function defineNetwork(bbb) {
  rollDice();
  if (bbb) {
    newBackground = true;
  }
  nodesToDraw = 1;
  bigdim = max(width, height);
  xygap = bigdim/float(_xydivs);
  maxDistance = dist(0, 0, bigdim, bigdim);
  sameWeight = xygap * _weightFactor;
  sameThinWeight = map(bigdim, 1920, 3840, 1.0, 2.0);
  sameThinWeight = max(0.5, sameThinWeight);
  if (_mirrorChance > 0.5 && _weightFactor > 0.1) {
    _mirror = true;
  } else {
    _mirror = false;
  }
  nodes = [(_xydivs+7)*(_xydivs+7)];
  nodeOrder = [nodes.length];
  for (let i = 0; i < nodeOrder.length; i++) {
    nodeOrder[i] = i;
  }
  let nodeCount = 0;
  let wonk = minWonk;
  if (_rwonk > 0.75) {
    wonk = maxWonk;
  } else if (_rwonk > 0.10) {
    wonk = _metaWonk;
  }
  for (let x = -3; x <= _xydivs+3; x++) {
    for (let y = -3; y <= _xydivs+3; y++) {
      let xoff = 0;
      if (y % 2 == 0) {
        xoff = -xygap/2.0;
      }
      let xx = r.rb(-xygap*wonk, xygap*wonk);
      let yy = r.rb(-xygap*wonk, xygap*wonk);
      let nx = map(x*xygap, 0, bigdim, -bigdim/2-xygap/2, bigdim/2+xygap/2);
      let ny = map(y*xygap, 0, bigdim, -bigdim/2-xygap/2, bigdim/2+xygap/2);
      nodes[nodeCount] = new Node(nx + xoff, ny, xx, yy);
      nodeCount++;
    }
  }
  shuffle(nodes, true);
  buildConnections();
}

function buildConnections() {
  for (let i = 0; i < nodes.length; i++) {
    nodes[i].setID(i);
  }
  for (let i = 0; i < nodes.length; i++) {
    nodes[i].buildNetwork(nodes);
  }
}

class Node {

  constructor(_x, _y, _xoff, _yoff) {
    this.x = _x + _xoff;
    this.y = _y + _yoff;
    this.newX = this.x;
    this.newY = this.y;
    this.centerDist = dist(0, 0, this.x, this.y);
    this.minimDist = dist(0, 0, xygap/2, xygap/2);
    this.totalConnections = 0;
    this.currentNet = 0;
    this.speed = [];
    this.weights = [];
    this.baseDist = dist(0, 0, xygap+(xygap/_xydivs), xygap+(xygap/_xydivs));
    this.id = 0;
    this.otherNodes = [];
    this.connections = [];
    this.alphaLines = [];
    this.sending = [];
    this.c = [ cyellow, cblue, cred, cwhite ];
    this.easing = 0.04;
    this.currentC = [];
    this.nextC = [];
  }

  setID(_id) { this.id = _id; }

  buildNetwork(_others) {
    let potentialConnections = [_others.length];
    let count = 0;
    for (let i = 0; i < _others.length; i++) {
      if (i != this.id) {
        let d = dist(this.x, this.y, _others[i].x, _others[i].y);
        if (d < this.baseDist) {
          potentialConnections[count] = _others[i];
          count++;
          this.totalConnections++;
        }
      }
    }

    this.connections = subset(potentialConnections, 0, count);

    this.alphaLines = [this.connections.length];
    this.sending = [this.connections.length];
    this.speed = [this.connections.length];
    this.currentC = [this.connections.length];
    this.nextC = [this.connections.length];
    this.weights = [this.connections.length];
    this.currentNet = int(r.rb(0, this.connections.length));

    let sharedColor = this.c[int(r.rb(0, this.c.length))];

    for (let i = 0; i < this.connections.length; i++) {
      this.alphaLines[i] = 1.0;
      this.sending[i] = true;
