float celullar(vec2 _uv,vec2 _iResolution,float _cellNum)
{
    float cellNum = _cellNum;

    vec2 uv = _uv;
    vec2 iResolution = _iResolution;
    uv.x *= iResolution.x / iResolution.y;

    uv *= cellNum;

    vec2 i = floor(uv);
    vec2 f = fract(uv);

    float minDist = 1.;

    for (int y = -1; y <= 1; y++)
    {
        for (int x = -1; x <= 1; x++)
        {
            vec2 neighbor = vec2(x, y);

            vec2 point = random2(i + neighbor);

            point = 0.5 + 0.5 * sin(6.2831 * point);

            float dist = length(neighbor + point - f);

            minDist = min(minDist, dist);
        }
    }

    return minDist;

}


