  { name: "Valencia", rgb: [231, 58, 16] }, //31
  { name: "Clown Nose", rgb: [211, 27, 0] }, //32
];

const grayscaleColors = [
  { name: "Black", rgb: [0, 0, 0], light: 0.1, ground: 0 },
  { name: "Dark Gray", rgb: [51, 51, 51], light: 0.1, ground: 0.28 },
  { name: "Medium Gray", rgb: [102, 102, 102], light: 0.15, ground: 0.525 },
  { name: "Light Gray", rgb: [153, 153, 153], light: 0.29, ground: 0.6 },
  { name: "Very Light Gray", rgb: [204, 204, 204], light: 0.37, ground: 0.71 },
  { name: "White", rgb: [255, 255, 255], light: 0.5, ground: 1 },
  { name: "Very Light Gray2", rgb: [204, 204, 204], light: 0.37, ground: 0.71 },
  { name: "Light Gray2", rgb: [153, 153, 153], light: 0.29, ground: 0.6 },
  { name: "Medium Gray2", rgb: [102, 102, 102], light: 0.15, ground: 0.525 },
  { name: "Dark Gray2", rgb: [51, 51, 51], light: 0.1, ground: 0.28 },
];

//************************************************************* calculate features and colours *************************************************************
function calculateFeatures() {
  //randomly picks small (0) or large (1) heart
  let heartSize = R.random_num(0, 1);
  heart = heartSize < 0.85 ? 0 : 1;

  const randomRarity = Math.min(R.random_num(0, 1), 0.99999999999999);

  const rarityLookup = [
    {
      rarity: FEATURE_STANDARD,
      upperBound: 0.8,
      strategies: [
        COLOR_TYPE_XGRAD,
        COLOR_TYPE_YGRAD,
        COLOR_TYPE_LINEAR,
        COLOR_TYPE_RADIAL,
        COLOR_TYPE_RADIAL,
        COLOR_TYPE_RANDOM,
        COLOR_TYPE_RANDOM,
        COLOR_TYPE_RANDOM,
      ],
    },
    {
      rarity: FEATURE_BI_COLOR,
      upperBound: 0.995,
      strategies: [
        COLOR_TYPE_XGRAD,
        COLOR_TYPE_YGRAD,
        COLOR_TYPE_LINEAR,
        COLOR_TYPE_RADIAL,
        COLOR_TYPE_RADIAL,
        COLOR_TYPE_RANDOM,
        COLOR_TYPE_RANDOM,
        COLOR_TYPE_RANDOM,
      ],
    },
    { rarity: FEATURE_HYPER, upperBound: 1, strategies: [COLOR_TYPE_RANDOM] },
  ];

  for (let i = 0; i < rarityLookup.length; i++) {
    if (randomRarity < rarityLookup[i].upperBound) {
      rarity = rarityLookup[i].rarity; // selected rarity
      colorType = R.random_choice(rarityLookup[i].strategies); // choose color strategies
      colorPalette = colors;
      if (R.random_num(0, 1) < 0.25 && rarity != FEATURE_HYPER)
        colorPalette = grayscaleColors;
      break;
    }
  }

  let reverse;
  // reverses the color palette 50% of the time
  if (R.random_bool(0.5)) {
    reverse = true;
    colors.reverse();
  } else {
    reverse = false;
  }

  if (rarity == FEATURE_HYPER) {
    hyperType = R.random_choice([0, 0, 0, 1, 1, 2]);
  }

  //picks a starting color for color selection processes below
  let startColor = R.random_int(0, colorPalette.length - 1);

  //picks how much range there will be in color
  let randomRange = R.random_num(0, 1);

  if (randomRange <= 0.2 && randomRange > 0.1) {
    spread = 1;
  } else if (randomRange <= 0.1) {
    spread = 0;
  } else {
    spread =
      colorPalette == grayscaleColors
        ? heart == 0
          ? 2
          : R.random_int(2, 5)
        : heart == 0
          ? R.random_int(2, 4)
          : R.random_int(2, 6);
  }

  signatureColor = grayscaleColors[5];
  if (R.random_num(0, 1) > 0.8)
    signatureColor = colors[R.random_int(0, colors.length - 1)];

  //picks how much space there is between chosen colors
  let speed =
    colorPalette == grayscaleColors
      ? heart == 0
        ? 0
        : R.random_int(0, 2)
      : heart == 0
        ? R.random_int(0, 2)
        : R.random_int(0, 3);
  //

  let confettiBool = R.random_num(0, 1) > 0.25;
  let glisten =
    rarity == FEATURE_BI_COLOR ||
      rarity == FEATURE_HYPER ||
      colorType != COLOR_TYPE_RANDOM ||
      spread > 0 ||
      speed > 0 ||
      heart == 1 ||
      !confettiBool
      ? false
      : R.random_num(0, 1) > 0.5;
  let glistenIsOpposite =
    R.random_num(0, 1) > (colorPalette == colors ? 0.75 : 0.25);
  let glistenSpace =
    colorPalette == "color"
      ? glistenIsOpposite
        ? R.random_int(1, 9)
        : R.random_int(3, 30)
      : glistenIsOpposite
        ? R.random_int(3, 30)
        : R.random_int(1, 9);
  let confetti = confettiBool && !glisten;
  let confettiRate = R.random_num(0.1, 0.5);
  let confettiVariability = R.random_num(0, 1) < 0.95 ? "low" : "high";
  let confettiStartColor = R.random_int(0, colors.length - 1);
  let confettiSpots = [];
  let outline =
    rarity == FEATURE_BI_COLOR ||
      rarity == FEATURE_HYPER ||
      confetti == true ||
      glisten == true
      ? false
      : heart == 0
        ? R.random_num(0, 1) > 0.95
        : R.random_num(0, 1) > 0.85;
  let outlineColor;
  let insideColors = [];
  if (outline) {
    let outlinePalette = R.random_choice([colors, grayscaleColors]);
    let outlineColorNumber =
      outlinePalette == colors ? R.random_int(0, 32) : R.random_int(0, 5);
    outlineColor = outlinePalette[outlineColorNumber].rgb;
    let heartGradient = R.random_bool(0.5);
    if (heartGradient && heart == 1) {
      for (let j = 0; j < 3; j++) {
        insideColors[j] =
          outlinePalette[
            (outlineColorNumber + j + 1 + speed * j) % outlinePalette.length
          ].rgb;
      }
    } else {
      //make sure second color is not the same as outline
      let outlineCount = 0;
      while (outlineCount < 1) {
        insideColors[0] = R.random_choice([
          colors[R.random_int(0, 32)].rgb,
          grayscaleColors[R.random_int(0, 5)].rgb,
        ]);
        if (insideColors[0] != outlineColor) {
          outlineCount++;
        }
      }
      //if large heart and not "gradientHeart" then last two colors can be random (and can be repeats)
      if (heart == 1) {
        for (let i = 1; i < 3; i++) {
          insideColors[i] = R.random_choice([
            colors[R.random_int(0, 32)].rgb,
            grayscaleColors[R.random_int(0, 5)].rgb,
          ]);
        }
      }
    }
  }

  let radStartX = heart == 0 ? R.random_int(0, 7) : R.random_int(0, 14);
  let radStartY = heart == 0 ? R.random_int(0, 6) : R.random_int(0, 12);

  let advanceColor = 0;

  // the number of cubes, 7 or 13 depending on the selected heart type
  cubesNum = heart == 0 ? 7 : 13;

  //assigns the cube heights and color to the heart matrix (checking neighbors), still need to assign top and bottom alternate colors
  for (let i = 0; i < hearts[heart].length; i++) {
    if (hearts[heart][i] == 0) {
      heartMatrix.push(0);
    } else {
      let ht1;
      let count = 0;
      let prevHeight = heartMatrix[i - 1].height;
      let aboveHeight = i < cubesNum ? 0 : heartMatrix[i - cubesNum].height;
      advanceColor++;

      while (count < 1) {
        ht1 = R.random_int(1, 6);
        if (ht1 != prevHeight && ht1 != aboveHeight) {
          count++;
        }
      }

      let x = i % cubesNum;
      let y = Math.floor(i / cubesNum);
      let thisColor = 0;
      let glistenColor;
      //assigns color per color type, can be cleaned up with a switch case, still need to add variability for color speed (how harsh the gradients and variability are)
      if (colorType == COLOR_TYPE_RADIAL) {
        let distance = dist(x, y, radStartX, radStartY);
        thisColor =
          (startColor +
            Math.round(distance) +
            Math.floor(R.random_int(0, spread)) +
            Math.round(speed * distance)) %
          colorPalette.length;
      } else if (colorType == COLOR_TYPE_XGRAD) {
        thisColor =
          (startColor + x + Math.floor(R.random_int(0, spread)) + speed * x) %
          colorPalette.length;
      } else if (colorType == COLOR_TYPE_YGRAD) {
        thisColor =
          (startColor + y + Math.floor(R.random_int(0, spread)) + speed * y) %
          colorPalette.length;
      } else if (colorType == COLOR_TYPE_LINEAR) {
        thisColor =
          (startColor +
            advanceColor +
            Math.floor(R.random_int(0, spread)) +
            speed * advanceColor) %
          colorPalette.length;
      } else {
        if (rarity == FEATURE_HYPER && hyperType == 0) {
          thisColor = R.random_int(0, 32) % colorPalette.length;
        } else {
          //oppMix pushes the range of opposing colors to greater distances from start color based on speed
          let oppMix = R.random_num(0, 1);
          thisColor =
            (startColor +
              (oppMix < 0.75
                ? R.random_int(0, spread)
                : speed * 5 + R.random_int(0, spread))) %
            colorPalette.length;
        }
      }

      let c;
      if (confetti) {
        let colorTrigger = R.random_num(0, 1);
        let confettiDistances = [];
        for (let i = 0; i < confettiSpots.length; i++) {
          confettiDistances.push(
            dist(x, y, confettiSpots[i][0], confettiSpots[i][1])
          );
        }
        let shortestDistance = Math.min(...confettiDistances);

        if (colorTrigger < confettiRate && shortestDistance > 1) {
          c =
            colorPalette == colors
              ? grayscaleColors[
                (confettiStartColor +
                  (confettiVariability == "low"
                    ? R.random_int(0, 2)
                    : R.random_int(0, 5))) %
                grayscaleColors.length
              ].rgb
              : colors[
                (confettiStartColor +
                  (confettiVariability == "low"
                    ? R.random_int(0, 5)
                    : R.random_int(0, 32))) %
                colors.length
              ].rgb;
          confettiSpots.push([x, y]);
        } else {
          c = colorPalette[thisColor].rgb;
        }
      } else {
        if (outline) {
          if (hearts[heart][i] == 1) {
            c = outlineColor;
          } else if (hearts[heart][i] == 2) {
            c = insideColors[0];
          } else if (hearts[heart][i] == 3) {
            c = insideColors[1];
          } else if (hearts[heart][i] == 4) {
            c = insideColors[2];
          }
        } else {
          c = colorPalette[thisColor].rgb;
        }
      }

      if (glisten) {
        thisColor = startColor;
        // pick glisten special colours

        if ((x === 4 && y === 3) || (x === 5 && y === 2)) {
          if (glistenIsOpposite) {
            if (colorPalette == grayscaleColors) {
              glistenColor = (thisColor + glistenSpace) % colors.length;
              c = colors[glistenColor].rgb;
            } else {
              glistenColor =
                (thisColor + glistenSpace) % grayscaleColors.length;
              c = grayscaleColors[glistenColor].rgb;
            }
          } else {
            glistenColor = (thisColor + glistenSpace) % colorPalette.length;
            c = colorPalette[glistenColor].rgb;
          }
        }
      }

      heartMatrix.push({ color: c, height: ht1 });
    }
  }

  // biColorType 0 = same palette, 1 opposite palette
  biColorType = R.random_int(0, 1);
  topOrSide = R.random_int(0, 1);
  let count = 0;

  if (spread == 0 && colorType == COLOR_TYPE_RANDOM && biColorType == 0) {
    let originalColor = heartMatrix[2].color;
    while (count < 1) {
      secondColor =
        biColorType == 0
          ? colorPalette == grayscaleColors
            ? grayscaleColors[R.random_int(0, 5)].rgb
            : colors[R.random_int(0, 32)].rgb
          : colorPalette == grayscaleColors
            ? colors[R.random_int(0, 32)].rgb
            : grayscaleColors[R.random_int(0, 5)].rgb;
      if (secondColor != originalColor) {
        count++;
      }
    }
  } else {
    secondColor =
      biColorType == 0
        ? colorPalette == grayscaleColors
          ? grayscaleColors[R.random_int(0, 5)].rgb
          : colors[R.random_int(0, 32)].rgb
        : colorPalette == grayscaleColors
          ? colors[R.random_int(0, 32)].rgb
          : grayscaleColors[R.random_int(0, 5)].rgb;
  }

  let features = {
    "Base Color Palette": colorPalette == colors ? "Colors" : "Grayscale",
    Size: heart == 0 ? "Small" : "Large",
    Type:
      rarity == FEATURE_STANDARD
        ? "Standard"
        : rarity == FEATURE_BI_COLOR
          ? "Bi-Color"
          : "Hyper",
    "Color Mode": outline
      ? "Outline"
      : glisten
        ? "Glisten"
        : hyperType == 0
          ? "Hyper"
          : hyperType == 1
            ? "Hyper+"
            : hyperType == 2
              ? "Hyper++"
              : speed == 0 &&
                spread == 0 &&
                confetti == false &&
                colorType == COLOR_TYPE_RANDOM &&
                rarity != FEATURE_BI_COLOR
                ? "Solid"
                : colorType,
    Confetti: confetti && rarity != FEATURE_HYPER ? true : false,
    Speed: outline || rarity == FEATURE_HYPER ? "N/A" : speed,
    Spread: outline || rarity == FEATURE_HYPER ? "N/A" : spread,
    "Signature color": signatureColor.name,
  };

  console.log(features);

  return features;
}

//**************************************************************************************************************************

function setup() {
  // initialise Random class
  R = new Random();
  // calculate traits and colours
  calculateFeatures();
  // setup 3d Scene
  setupScene();
  // generate heart
  generateHeart();

  if (isMobileOrTablet()) {
    addInstructionIcon();
    addRotationIcon();
  }
}

// setup three.js scene
function setupScene() {
  // Create a scene
  scene = new THREE.Scene();
  scene.background = new THREE.Color(0xffffff);

  // Create a camera
  camera = new THREE.PerspectiveCamera(
    65,
    window.innerWidth / window.innerHeight,
    0.1,
    1000
  );
  camera.position.z = heart == 0 ? 12 : 17;
  camera.position.y = 1;

  // LIGHTS
  hemiLight = new THREE.HemisphereLight(0xffffff, 0xffffff, 1.7);
  hemiLight.color.setHSL(0, 0, 0.4);
  //hemiLight.groundColor.setHSL(0.095, 1, 0.75);
  hemiLight.position.set(0, 50, 0);
  scene.add(hemiLight);

  const hemiLightHelper = new THREE.HemisphereLightHelper(hemiLight, 10);
  //scene.add(hemiLightHelper);

  const dirLight = new THREE.DirectionalLight(0xffffff, 1);
  //dirLight.color.setHSL(0.1, 1, 0.95);
  dirLight.position.set(-1.3, 0.75, 0.5);
  dirLight.position.multiplyScalar(30);
  dirLight.shadow.radius = 10;
  dirLight.shadow.blurSamples = 35;
  scene.add(dirLight);

  dirLight.castShadow = true;

  const d = 10;
  dirLight.shadow.camera.left = -d;
  dirLight.shadow.camera.right = d;
  dirLight.shadow.camera.top = d;
  dirLight.shadow.camera.bottom = -d;
  dirLight.shadow.mapSize.set(2048, 2048);

  const dirLightHelper = new THREE.DirectionalLightHelper(dirLight, 10);
  // scene.add(dirLightHelper);

  // GROUND
  const groundGeo = new THREE.PlaneGeometry(100, 100);
  groundMat = new THREE.MeshPhysicalMaterial({
    color: "white",
    metalness: 0,
    roughness: 1,
  });

  ground = new THREE.Mesh(groundGeo, groundMat);
  groundPosY = heart == 0 ? -5 : -9;
  ground.position.y = groundPosY;

  ground.rotation.x = -Math.PI / 2;
  ground.receiveShadow = true;
  scene.add(ground);

  // Create renderer
  renderer = new THREE.WebGLRenderer({
    antialias: true,
    preserveDrawingBuffer: true,
    alpha: false,
  });
  renderer.setPixelRatio(window.devicePixelRatio);
  renderer.setSize(window.innerWidth, window.innerHeight);
  document.body.appendChild(renderer.domElement);
  //renderer.toneMapping = THREE.ReinhardToneMapping;
  renderer.shadowMap.enabled = true;
  renderer.shadowMap.type = THREE.PCFSoftShadowMap;

  // Create orbit controls
  controls = new OrbitControls(camera, renderer.domElement);
  controls.minAzimuthAngle = 0;
  controls.maxAzimuthAngle = 0;
  controls.minPolarAngle = Math.PI / 6;
  controls.maxPolarAngle = Math.PI / 1.7;
  controls.maxDistance = 25;
  controls.minDistance = 7;
  controls.enablePan = false;

  changeBackground();

  createSemiCircleOfBoxes(scene);

  createSignature();
  // Start animation loop
  animate();
}

window.addEventListener("resize", onWindowResize, false);

function onWindowResize() {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.position.z = heart == 0 ? 12 : 17;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
}

// Render loop
function animate() {
  requestAnimationFrame(animate);

  //stats.begin();

  if (!isAnimating && !instructionsEnabled) {
    selectedCube = false;
    // update the picking ray with the camera and pointer position
    raycaster.setFromCamera(pointer, camera);

    // calculate objects intersecting the picking ray
    const intersects = raycaster.intersectObjects(cubesArray, true);

    if (intersects.length > 0) {
      if (intersects[0].object) {
        selectedCube = intersects[0].object.parent;
      }
    }
  }

  // update animation
  updateAnimation();

  // Update controls
  controls.update();

  // Render the scene
  if (envContainer) {
    envContainer.visible = true;
    envContainer.rotation.y += 0.0005; // slightly rotate the invisible boxes to make the metallic reflections feel alive
    cubeCamera1.update(renderer, scene);
    envContainer.visible = false;
  }

  if (heartContainer && signatureMesh) {
    if (window.innerWidth < 600) {
      heartContainer.position.z = -8;
    } else {
      heartContainer.position.z = 0;
    }
    if (isSpinning) heartContainer.rotation.y += spinningSpeed;

    let diff = Math.floor(heartContainer.rotation.y / (Math.PI * 2));
    let rot = heartContainer.rotation.y - Math.PI * 2 * diff;
    //console.log(heartContainer.rotation.y, diff, rot);

    if (rot > 1 && rot < 5 && !instructionsEnabled) {
      signatureMesh.visible = true;
    } else {
      signatureMesh.visible = false;
    }
  }

  if (instructionsEnabled) {
    camera.position.z = heart == 0 ? 15 : 23;
    animateInstructions();
  } else {
    camera.position.z = heart == 0 ? 12 : 17;
  }

  renderer.render(scene, camera);

  //stats.end();
}

function animateInstructions() {
  if (whiteCube) scene.remove(whiteCube);
  if (paintCube) scene.remove(paintCube);

  switch (activeInstructionStep) {
    case 0:
      generateWhiteCube();
      whiteCube.rotation.y += 0.001;
      break;
    case 2:
      generatePaintCube();
      paintCube.rotation.y += 0.001;
      break;
    case 3:
      heartContainer.rotation.y = 0;

      animateAssemble();
      break;
  }
}

function generateHeart() {
  // centers heart
  let w = cubesNum * cubeScale;
  let h = cubesNum * cubeScale;

  scene.add(heartContainer);

  for (let j = 0; j < cubesNum - 1; j++) {
    for (let k = 0; k < cubesNum; k++) {
      let hmi = k + j * cubesNum;

      ht = cubeHeights[heartMatrix[hmi].height] * cubeScale;
      let ind = cubeHeights.indexOf(ht);
      let size = ind !== -1 ? cubeHeightsNames[ind] : "Unknown";
      let cubeContainer = new THREE.Group();
      let dataObject = {};
      let clrName = "";

      if (heartMatrix[hmi] != 0) {
        // Create cubes
        let spacing = 0.11;
        switch (rarity) {
          case FEATURE_HYPER:
            if (hyperType == 0) {
              let cubeColor = R.random_int(0, 32);
              let clrName = findColorNameByRGB(colors[cubeColor].rgb);
              const cube = getCube(
                cubeScale,
                cubeScale,
                ht,
                colors[cubeColor].rgb
              );
              dataObject = {
                id: hmi,
                size: size,
                face: clrName,
                left: clrName,
                right: clrName,
                bottom: clrName,
                top: clrName,
                back: clrName,
              };
              cubeContainer.data = dataObject;
              cubeContainer.add(cube);
              cube.position.set(0, 0, ht / 2);
            } else if (hyperType == 1) {
              let color1 = R.random_int(0, 32);
              const cube = getCube(

     cubeScale,
                cubeScale,
                ht,
                colors[color1].rgb
              );
              cubeContainer.add(cube);
              cube.position.set(0, 0, ht / 2);

              let color2 = R.random_int(0, 32);
              const cube2 = getCube(
                cubeScale,
                cubeScale,
                0.001,
                colors[color2].rgb
              );
              cubeContainer.add(cube2);
              cube2.position.set(0, 0, ht + 0.001);

              dataObject = {
                id: hmi,
                size: size,
                face: findColorNameByRGB(colors[color2].rgb),
                left: findColorNameByRGB(colors[color1].rgb),
                right: findColorNameByRGB(colors[color1].rgb),
                bottom: findColorNameByRGB(colors[color1].rgb),
                top: findColorNameByRGB(colors[color1].rgb),
                back: findColorNameByRGB(colors[color1].rgb),
              };
              cubeContainer.data = dataObject;
            } else if (hyperType == 2) {
              let hyperColors = [];
              dataObject = { id: hmi, size: size };

              for (let i = 0; i < 6; i++) {
                const color = getRandomColor();
                console.log("thisis color:" + color);
                hyperColors.push(color);
                dataObject[colorNames[i]] = findColorNameByRGB(color);
              }
              cubeContainer = getHyperCube(ht, hyperColors);
            }

            cubeContainer.data = dataObject;
            heartContainer.add(cubeContainer);
            cubesArray.push(cubeContainer);

            break;

          case FEATURE_BI_COLOR:
            let clr = heartMatrix[hmi].color;
            const baseColor = topOrSide == 0 ? secondColor : clr;
            const topColor = topOrSide == 0 ? clr : secondColor;

            const cube = getCube(cubeScale, cubeScale, ht, baseColor);
            cubeContainer.add(cube);
            cube.position.set(0, 0, ht / 2);

            const cube2 = getCube(cubeScale, cubeScale, 0.001, topColor);
            cubeContainer.add(cube2);
            cube2.position.set(0, 0, ht + 0.001);

            clrName = findColorNameByRGB(baseColor);

            let topClrName = findColorNameByRGB(topColor);
            dataObject = {
              id: hmi,
              size: size,
              face: topClrName,
              left: clrName,
              right: clrName,
              bottom: clrName,
              top: clrName,
              back: clrName,
            };
            cubeContainer.data = dataObject;
            heartContainer.add(cubeContainer);
            cubesArray.push(cubeContainer);
            break;

          default:
            clrName = findColorNameByRGB(heartMatrix[hmi].color);

            heartContainer.add(cubeContainer);

            const cubeNormal = getCube(
              cubeScale,
              cubeScale,
              ht,
              heartMatrix[hmi].color
            );
            cubeNormal.position.set(0, 0, ht / 2);
            cubeContainer.add(cubeNormal);
            dataObject = {
              id: hmi,
              size: size,
              face: clrName,
              left: clrName,
              right: clrName,
              bottom: clrName,
              top: clrName,
              back: clrName,
            };
            cubeContainer.data = dataObject;
            cubesArray.push(cubeContainer);

            break;
        }

        cubeContainer.position.set(
          (!heart ? 0.15 : -0.15) -
          (cubesNum / 2) * cubeScale +
          (k * cubeScale + spacing * k),
          -j * cubeScale + spacing * -j,
          0
        );
        cubeData[hmi] = dataObject;
      } else {
        dataObject = {
