function getHyperCube(depth, colors) {
  //'face', 'left', 'right', 'bottom', 'top', 'back'
  let spacing = 0.015;
  const hyperCube = new THREE.Group();
  let fc = getCube(cubeScale, cubeScale, 0.001, colors[0]);
  fc.position.set(0, 0, depth);
  hyperCube.add(fc);

  let lc = getCube(depth - spacing * 2, cubeScale, 0.001, colors[1]);
  lc.position.set(-cubeScale / 2 - spacing, 0, depth / 2);
  lc.rotation.set(0, Math.PI / 2, 0);
  hyperCube.add(lc);

  let rc = getCube(depth - spacing * 2, cubeScale, 0.001, colors[2]);
  rc.position.set(cubeScale / 2 + spacing, 0, depth / 2);
  rc.rotation.set(0, Math.PI / 2, 0);
  hyperCube.add(rc);

  let btc = getCube(cubeScale, depth - spacing * 2, 0.001, colors[3]);
  btc.position.set(0, -cubeScale / 2 - spacing, depth / 2);
  btc.rotation.set(Math.PI / 2, 0, 0);
  hyperCube.add(btc);

  let tc = getCube(cubeScale, depth - spacing * 2, 0.001, colors[4]);
  tc.position.set(0, cubeScale / 2 + spacing, depth / 2);
  tc.rotation.set(Math.PI / 2, 0, 0);
  hyperCube.add(tc);

  let bc = getCube(cubeScale, cubeScale, 0.001, colors[5]);
  bc.position.set(0, 0, 0);
  hyperCube.add(bc);

  return hyperCube;
}

function getCube(width, height, depth, rgb) {
  //const envMap = cubeRenderTarget1.texture;
  let metalness = 0.5;
  let roughness = 0.5;
  let emissive = 0;
  const geometry = createRoundedBoxGeometry(width, height, depth, 0.01);
  const material = new THREE.MeshPhysicalMaterial({
    color: new THREE.Color(rgb[0] / 255, rgb[1] / 255, rgb[2] / 255),
    emissive: emissive,
    roughness: roughness,
    metalness: metalness,
    reflectivity: 1,
  });
  const cube = new THREE.Mesh(geometry, material);
  cube.castShadow = true;
  cube.receiveShadow = true;
  return cube;
}

function enableMetallic(state) {
  changeMaterials(state, 1, 0.2, 1, 0);
}

function changeMaterials(state, metalness, roughness, reflectivity, emissive) {
  const envMap = cubeRenderTarget1.texture;
  cubesArray.forEach((cube) => {
    cube.traverse(function (child) {
      if (child instanceof THREE.Mesh) {
        if (state) {
          // apply special material
          child.material.metalness = metalness;
          child.material.roughness = roughness;
          child.material.reflectivity = reflectivity;
          child.material.emissive = emissive;
          child.material.envMap = envMap;
          child.material.envMapIntensity = 1;
        } else {
          // reset to default material
          child.material.metalness = 0.5;
          child.material.roughness = 0.5;
          child.material.reflectivity = 0;
          child.material.emissive = 0;
          child.material.envMapIntensity = 0;
        }
      }
    });
  });
}

function createRoundedBoxGeometry(width, height, depth, r) {
  let shape = new THREE.Shape();
  let angleStep = Math.PI * 0.5;

  shape.absarc(width / 2, height / 2, r, angleStep * 0, angleStep * 1);
  shape.absarc(-width / 2, height / 2, r, angleStep * 1, angleStep * 2);
  shape.absarc(-width / 2, -height / 2, r, angleStep * 2, angleStep * 3);
  shape.absarc(width / 2, -height / 2, r, angleStep * 3, angleStep * 4);

  let g = new THREE.ExtrudeGeometry(shape, {
    depth: depth,
    bevelEnabled: true,
    bevelThickness: 0.05,
    bevelSize: 0.05,
    bevelSegments: 4,
    curveSegments: 4,
  });
  g.center();
  return g;
}

// utils functions
function dist(x1, y1, x2, y2) {
  const dx = x2 - x1;
  const dy = y2 - y1;
  return Math.sqrt(dx * dx + dy * dy);
}

function easeInOutSine(x) {
  return -(Math.cos(Math.PI * x) - 1) / 2;
}

