function changeBackground() {
  backgroundIndex =
    (backgroundIndex + backgroundChangeDir + grayscaleColors.length) %
    grayscaleColors.length;
  backgroundChangeDir *=
    backgroundIndex === 0 || backgroundIndex === grayscaleColors.length
      ? -1
      : 1;

  const { rgb, light, ground } = grayscaleColors[backgroundIndex];

  hemiLight.color.setHSL(0, 0, light);
  groundMat.color.setHSL(0, 0, ground);

  const normalizedColor = rgb.map((value) => value / 255);
  const color = new THREE.Color().fromArray(normalizedColor);

  scene.background = color;
  renderer.setClearColor(color);
}

function saveCanvas(width, height, fileName) {
  if (instructionsEnabled) showInstructions();

  const originalSize = {
    width: renderer.getSize().width,
    height: renderer.getSize().height,
  };

  const originalAspect = camera.aspect;
  renderer.setSize(width, height);
  camera.aspect = width / height;
  camera.updateProjectionMatrix();

  renderer.render(scene, camera);

  const canvasDataURL = renderer.domElement
    .toDataURL()
    .replace("image/png", "image/octet-stream");
  const a = document.createElement("a");
  a.href = canvasDataURL;
  a.download = fileName;
  a.click();

  renderer.setSize(originalSize.width, originalSize.height);
  camera.aspect = originalAspect;
  camera.updateProjectionMatrix();
  renderer.render(scene, camera);

}

function downloadGLB(group, fileName) {
  resetCubePositions();
  signatureMesh.visible = true;

  const gltfExporter = new GLTFExporter();

  gltfExporter.parse(
    group,
    function (gltf) {
      const blob = new Blob([gltf], { type: "application/octet-stream" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.style.display = "none";
      link.href = url;
      link.download = fileName || "heart.glb";

      document.body.appendChild(link);
      link.click();

      // Clean up
      URL.revokeObjectURL(url);
      document.body.removeChild(link);
    },
    {
      binary: true, // Export as binary GLB
      embedImages: true, // Embed images in the GLB
    }
  );
}

