// animation

// Assuming you have a reusable function called `moveCube` that takes
// the cube object, a boolean `state`, and the animationProgress value.

function updateAnimation() {
  // floating animation
  if (heartContainer && isFloating) {
    heartContainer.position.y =
      -2.5 - groundPosY + Math.sin(clock.getElapsedTime()) / 4;
  }

  if (!isAnimating) return;

  if (animationProgress < 0.999) {
    animationProgress += 0.05;

    const normalizedProgress = Math.min(animationProgress, 1); // Ensure progress doesn't exceed 1
    cubesArray.forEach((cube) => {
      if (cube !== selectedCube) {
        moveCube(cube, false, normalizedProgress);
      }
    });
    moveCube(selectedCube, !selectedCube.isActive, normalizedProgress);
  } else {
    selectedCube.isActive = !selectedCube.isActive;
    animationProgress = 0;
    isAnimating = false;
  }
}

function onPointerMove(event) {
  pointer = getNormalizedPointerPosition(event, renderer.domElement);

  if (mouseDown) {
    let offset = lastPointerX - pointer.x;
    const multipler = 3;
    heartContainer.rotation.y = lastRotationY - offset * multipler;
  }
}

function getNormalizedPointerPosition(event, canvas) {
  // calculate pointer position in normalized device coordinates
  // (-1 to +1) for both components
  const rect = canvas.getBoundingClientRect();
  const canvasX = event.clientX - canvas.offsetLeft;
  const canvasY = event.clientY - canvas.offsetTop;
  const normalizedX = (canvasX / rect.width) * 2 - 1;
  const normalizedY = -((canvasY / rect.height) * 2 - 1); // Invert the Y-axis to match Three.js coordinate system

  return new THREE.Vector2(normalizedX, normalizedY);
}

function onPointerClick() {
  if (selectedCube && !instructionsEnabled) {
    animationProgress = 0;
    isAnimating = true;
    showInfo(selectedCube.data);
    beep();
  }
}

function detectTapAndHold(element, duration, callback) {
  let timeoutId;

  element.addEventListener("touchstart", function (event) {
    timeoutId = setTimeout(function () {
      callback();
    }, duration);

    event.preventDefault(); // Prevent default touch behavior
  });

  element.addEventListener("touchend", function () {
    clearTimeout(timeoutId);
  });
}

function moveCube(object, state, progress) {
  let activePosition = 3;
  let targetPosition = activePosition;

  let position = easeInOutSine(progress) * targetPosition;
  if (!state) {
    targetPosition = 0;
    position = activePosition - easeInOutSine(progress) * activePosition;
  }

  if (targetPosition !== object.position.z) object.position.z = position;
}

function onMouseDown() {
  ++mouseDown;
  lastPointerX = pointer.x;
  lastRotationY = heartContainer.rotation.y;
}

function onMouseUp() {
  --mouseDown;
}

