function findColorNameByRGB(rgb) {
  for (const color of colors) {
    if (JSON.stringify(color.rgb) === JSON.stringify(rgb)) {
      return color.name;
    }
  }
  for (const color of grayscaleColors) {
    if (JSON.stringify(color.rgb) === JSON.stringify(rgb)) {
      return color.name;
    }
  }
  return "Color not found";
}

function findRgbByColorName(name) {
  for (const color of colors) {
    if (color.name === name) {
      return color.rgb;
    }
  }
  for (const color of grayscaleColors) {
    if (color.name === name) {
      return color.rgb;
    }
  }
  return "RGB not found";
}

