      const cell = row.insertCell();
      const id = i * 3 + j;
      const occurrences = countOccurrences(sizes, names[id]);

      cell.textContent = `${names[id]} - ${occurrences}`;
      cell.style.cssText = `
                border: 0;
                padding: 0.3vh;
                padding-right:1vh;
                text-align: left;
                color: white;
                font-family: Arial, Helvetica, sans-serif;
                font-size: 1.4vh;
            `;
    }
  }

  return table;
}

function scaleColorGrid(
  colorGrid = document.getElementById("color_grid"),
  content = document.getElementById("content")?.parentElement
) {
  if (!colorGrid || !content) {
    return;
  }

  const colorGridDims = colorGrid.getBoundingClientRect();
  const contentDims = content.getBoundingClientRect();

  const scaled = parseFloat(
    (colorGrid.style.transform || `scale(1)`).replace(/[^\d|\.]/g, "")
  );
  const gridWidth = colorGridDims.width / scaled;
  const gridHeight =
    colorGrid.childNodes.length === 3
      ? colorGridDims.height / scaled
      : (colorGridDims.height * 1.3) / scaled;

  const maxHeight = contentDims.top - colorGridDims.top;
  const maxWidth = window.innerWidth;

  colorGrid.style.transform = `scale(${Math.min(
    maxWidth / gridWidth,
    maxHeight / gridHeight
  )})`;
}

function createColorGrid() {
  const gridContainer = document.createElement("div");
  gridContainer.id = "color_grid";
  gridContainer.classList.add("color-grid");
  gridContainer.style.cssText = `
        margin-top: 15vh;
        position: absolute;
        top: 0%;
        left: 0%;
        padding: 5%;
        width: 46.2vh;
        justify-content: center;
        left: 50%;
        translate: -50% 0%;
        transform-origin: 50% 0%;
    `;

  scaleColorGrid(gridContainer);
  window.removeEventListener("resize", () => scaleColorGrid());
  window.addEventListener("resize", () => scaleColorGrid());

  const colorContainer = document.createElement("div");
  colorContainer.style.textAlign = "center";
  colorContainer.style.width = "100%";

  const grayscaleContainer = document.createElement("div");
  grayscaleContainer.style.textAlign = "center";
  grayscaleContainer.style.width = "100%";

  const colorArray = [...colors, ...grayscaleColors.slice(0, -4)];

  colorArray.forEach((color, id) => {
    const square = document.createElement("div");
    square.classList.add("color-square");
    const isGrayscale = id > colors.length - 1;
    const container = isGrayscale ? grayscaleContainer : colorContainer;

    square.style.cssText = `
            width: 3.2vh;
            height: 3.2vh;
            display: inline-block;
            margin-right: 1vh;
            margin-bottom: 1vh;
            cursor: pointer;
            color: white;
            text-align: center;
            line-height: 3.2vh;
            vertical-align: top;
            border-radius: 0.5vh;
            font-size: 2vh;
            background-color: rgb(${color.rgb.join(",")});
            ${color.rgb.toString() === "255,255,255"
        ? "border: 1px solid #cccccc; color: #000000;"
        : ""
      }
        `;

    if (findColorMatch(color.name)) {
      colorForPaintCube = color;
      if (id > colors.length - 1) colorForPaintCube = { rgb: [50, 50, 50] }; // if the colors are grayscale choose a dark gray
      square.innerHTML = "&hearts;";
    }

    square.addEventListener("mouseover", () => {
      showColorInfo(gridContainer, color.name, color.rgb);
    });

    container.appendChild(square);
  });

  gridContainer.append(colorContainer, grayscaleContainer);
  return gridContainer;
}

function showColorInfo(container, name, rgb) {
