let accumulatedTime = 0; // global counter
const timeThreshold = 2; // time it takes to go from 0 to max height, adjust as needed

function generateWhiteCube() {
  let rotY = 0;
  if (whiteCube) {
    rotY = whiteCube.rotation.y;
    scene.remove(whiteCube);
  }

  accumulatedTime += clock.getDelta();

  if (accumulatedTime >= timeThreshold) {
    accumulatedTime = 0; // reset counter
  }

  // Calculate the height based on linear interpolation
  const heightFactor = (accumulatedTime / timeThreshold) * 3.5;

  whiteCube = getCube(4, heightFactor, 4, [200, 200, 200]);
  scene.add(whiteCube);
  whiteCube.position.set(0, 2, 0);
  whiteCube.rotation.x = Math.PI / 12;
  whiteCube.rotation.y = rotY;
}

function generatePaintCube() {
  const rotY = paintCube ? paintCube.rotation.y : 0;

  const durationPerStep = 2; // 2 seconds for each step, change as desired
  const step = Math.floor(clock.getElapsedTime() / durationPerStep) % 4; // 4 steps in total: 0, 1, 2, 3

  let mixProgress;
  switch (step) {
    case 0:
      mixProgress = 0;
      break; // All white
    case 1:
      mixProgress = 0.33;
      break; // 33% alpha
    case 2:
      mixProgress = 0.66;
      break; // 66% alpha
    case 3:
      mixProgress = 1;
      break; // 100% alpha
  }

  const mixedColor = mixColors(
    { r: 255, g: 255, b: 255 },
    {
      r: colorForPaintCube.rgb[0],
      g: colorForPaintCube.rgb[1],
      b: colorForPaintCube.rgb[2],
    },
    mixProgress
  );

  if (paintCube) scene.remove(paintCube);
  paintCube = getCube(4, 4, 4, [mixedColor.r, mixedColor.g, mixedColor.b]);
  paintCube.position.set(0, 2, 0);
  paintCube.rotation.x = Math.PI / 12;
  paintCube.rotation.y = rotY;
  scene.add(paintCube);
}

function mixColors(color1, color2, progress) {
  progress = Math.min(1, Math.max(0, progress));

  const r = Math.round(color1.r + (color2.r - color1.r) * progress);
  const g = Math.round(color1.g + (color2.g - color1.g) * progress);
  const b = Math.round(color1.b + (color2.b - color1.b) * progress);

  return { r, g, b };
}

function animateAssemble() {
  let rowId = heartRowsArray.length;
  let middleRow = Math.round(heartRowsArray.length / 2 + 1);
  let speed = 40;
  heartRowsArray.forEach((row) => {
    row.forEach((cube) => {
      if (assembleProgress == 0) {
        if (rowId == middleRow) {
          cube.position.x = cube.trgX * 1000;
          cube.position.y = 6;
        } else if (rowId < middleRow) {
          cube.position.x = cube.trgX;
          cube.position.y = -160000 - rowId * 120000;
        } else {
          cube.position.x = cube.trgX;
          cube.position.y = 160000 + rowId * 120000;
        }
      } else {
        let trgY;
        let trgX;
        if (rowId == middleRow) {
          trgY = cube.trgY;
          trgX = cube.trgX;
          speed = 40;
          cube.position.y += (trgY - cube.position.y) / speed;
          cube.position.x += (trgX - cube.position.x) / speed;
        } else {
          speed = 40 + Math.abs(middleRow - rowId);
          trgY = cube.trgY;
          trgX = cube.trgX;
          cube.position.y += (trgY - cube.position.y) / speed;
          cube.position.x += (trgX - cube.position.x) / speed;
        }
      }
    });

    rowId -= 1;
  });

  assembleProgress += 0.005;
  if (assembleProgress > 6) assembleProgress = 0;
}

function resetCubePositions() {
  heartRowsArray.forEach((row) => {
    row.forEach((cube) => {
      cube.position.x = cube.trgX;
      cube.position.y = cube.trgY;
      cube.position.z = 0;
    });
  });
}

function separateCubesByY(cubesArray) {
  const rows = {};

  cubesArray.forEach((cube) => {
    const yValue = cube.position.y;
    if (!rows[yValue]) {
      rows[yValue] = [];
    }
    cube.trgY = yValue;
    cube.trgX = cube.position.x;
    rows[yValue].push(cube);
  });

  const separatedRows = Object.values(rows);

  return separatedRows;
}

