function setup() {
  let size = min(windowWidth, windowHeight);
  createCanvas(size, size, WEBGL);
  colorMode(HSB, 360, 100, 100); // Set color mode to HSB
  cubeSize = min(width, height) * 0.6; // Dynamically set cube size
  cubeHue = random(360); // Initialize cube hue here
  numSpheres = floor(random(1, 7)); // Randomly choose between 3 to 6 spheres
  rotationSpeed = random(0.005, 0.02); // Adjust the range as needed
  let sphereRadius = random(cubeSize / 12, cubeSize / 8);
  let buffer = sphereRadius * 1.5; // Buffer to keep spheres away from the walls
  for (let i = 0; i < numSpheres; i++) {
    let attempts = 0;
    let maxAttempts = 100; // Maximum attempts to find a suitable position
    let validPosition = false;
    let x, y, z;
    while (!validPosition && attempts < maxAttempts) {
      x = random(-cubeSize / 2 + buffer, cubeSize / 2 - buffer);
      y = random(-cubeSize / 2 + buffer, cubeSize / 2 - buffer);
      z = random(-cubeSize / 2 + buffer, cubeSize / 2 - buffer);
      validPosition = true;
      for (let sphere of spheres) {
        if (dist(x, y, z, sphere.pos.x, sphere.pos.y, sphere.pos.z) < sphereRadius * 3) {
          validPosition = false;
          break;
        }
      }
      attempts++;
    }
    if (validPosition) {
      spheres.push(new Sphere(x, y, z, sphereRadius));
    } else {
      console.warn("Failed to find a valid position for sphere after " + maxAttempts + " attempts.");
    }
    // Initialize stars
    createStars(); // Call the new createStars function
  }
}
function windowResized() {
