function createStars() {
  let starCount = floor(random(500, 2000)); // Random number of stars
  for (let i = 0; i < starCount; i++) {
    let star;
    do {
      star = createVector(random(-width, width), random(-height, height), random(-width, width));
    } while (abs(star.x) < cubeSize / 2 && abs(star.y) < cubeSize / 2 && abs(star.z) < cubeSize / 2);
    stars[i] = { pos: star, hue: random(360), size: random(2, 5) }; // Random size
  }
}

function draw() {
  // Oscillations in X and Y axes to form an infinity-like pattern
  cameraRotationY = sin(frameCount * rotationSpeed) * PI / 2;
  cameraRotationX = cos(frameCount * rotationSpeed) * PI / 2;

  // Calculate camera position based on rotations
  let fov = PI / 3; // Field of view (45 degrees)
  let cubeDiagonal = sqrt(3) * cubeSize / 2; // Diagonal of the cube
  let camDist = cubeDiagonal / tan(fov / 2); // Adjust distance to keep cube fully visible
  let camX = sin(cameraRotationY) * camDist;
  let camZ = cos(cameraRotationY) * camDist;
  let camY = sin(cameraRotationX) * camDist;

  // Set camera
  camera(camX, camY, camZ, 0, 0, 0, 0, 1, 0);

  // Adjust the perspective to ensure the entire cube is visible
  let aspect = width / height; // Aspect ratio
  perspective(PI / 3, aspect, 10, 10000); // Adjust near and far clipping with updated values

  background(0);
