function handleSphereCollisions() {
  for (let i = 0; i < spheres.length; i++) {
    for (let j = i + 1; j < spheres.length; j++) {
      let d = dist(spheres[i].pos.x, spheres[i].pos.y, spheres[i].pos.z, spheres[j].pos.x, spheres[j].pos.y, spheres[j].pos.z);
