function renderCube(size) {
  push();
  cubeHue += 5; // Adjust this value to change the speed of hue oscillation
  if (cubeHue >= 360) cubeHue -= 360;
  strokeWeight(5); // Set the stroke weight for the cube lines

  // Define the vertices of the cube
  let vertices = [
    [size / 2, size / 2, size / 2],
    [size / 2, -size / 2, size / 2],
    [size / 2, -size / 2, -size / 2],
    [size / 2, size / 2, -size / 2],
    [-size / 2, size / 2, size / 2],
    [-size / 2, -size / 2, size / 2],
    [-size / 2, -size / 2, -size / 2],
    [-size / 2, size / 2, -size / 2]
  ];

  // Define the edges of the cube
  let edges = [
    [0, 1], [1, 2], [2, 3], [3, 0],
    [4, 5], [5, 6], [6, 7], [7, 4],
    [0, 4], [1, 5], [2, 6], [3, 7]
  ];

  for (let edge of edges) {
    let start = vertices[edge[0]];
    let end = vertices[edge[1]];
    let hueValue = (cubeHue + map(edge[0], 0, vertices.length, 0, 360)) % 360; // Calculate the hue value
    stroke(hueValue, 100, 100); // Set the hue for the stroke color
    lineVertices(...start, ...end, hueValue);
  }

  pop();
}

// Function to draw line vertices with gradient
function lineVertices(x1, y1, z1, x2, y2, z2, hueOffset) {
  let segments = 7; // Number of segments for the gradient
  for (let i = 0; i < segments; i++) {
    let startHue = (cubeHue + hueOffset + map(i, 0, segments, 0, 360)) % 360; // Starting hue
    let startX = lerp(x1, x2, i / segments);
    let startY = lerp(y1, y2, i / segments);
    let startZ = lerp(z1, z2, i / segments);
    let endX = lerp(x1, x2, (i + 1) / segments);
    let endY = lerp(y1, y2, (i + 1) / segments);
    let endZ = lerp(z1, z2, (i + 1) / segments);

    stroke(startHue, 100, 100); // Set the stroke color
    line(startX, startY, startZ, endX, endY, endZ); // Draw a line segment
  }
}

class Sphere {
  constructor(x, y, z, r) {
    this.pos = createVector(x, y, z);
    let minSpeed = 3; // Minimum speed
    let maxSpeed = 20; // Maximum speed
  
    this.vel = createVector(
      constrain(noise(random(1000)) * 6 - 3, minSpeed, maxSpeed),
      constrain(noise(random(1000)) * 6 - 3, minSpeed, maxSpeed),
      constrain(noise(random(1000)) * 6 - 3, minSpeed, maxSpeed)
    );
  
    // Ensure velocity points away from closest walls
    let buffer = cubeSize / 4; // Adjust this value as needed
    if (abs(this.pos.x) > buffer) this.vel.x *= Math.sign(this.pos.x);
    if (abs(this.pos.y) > buffer) this.vel.y *= Math.sign(this.pos.y);
    if (abs(this.pos.z) > buffer) this.vel.z *= Math.sign(this.pos.z);


    // Adjust the velocity to ensure spheres move away from the wall
    if (this.pos.x + this.r > cubeSize / 2) this.vel.x = -abs(this.vel.x);
    if (this.pos.x - this.r < -cubeSize / 2) this.vel.x = abs(this.vel.x);
    if (this.pos.y + this.r > cubeSize / 2) this.vel.y = -abs(this.vel.y);
    if (this.pos.y - this.r < -cubeSize / 2) this.vel.y = abs(this.vel.y);
    if (this.pos.z + this.r > cubeSize / 2) this.vel.z = -abs(this.vel.z);
    if (this.pos.z - this.r < -cubeSize / 2) this.vel.z = abs(this.vel.z);

    this.r = r;
    this.bruiseCount = 0; // Initialize bruise count
    this.bruises = []; // Initialize as an array
    this.hue = random(360); // Initialize hue for gradient
  }

  render() {
    push();
    translate(this.pos.x, this.pos.y, this.pos.z);
    noStroke();
    let detail = 24; // Increase for a smoother sphere
    for (let i = 0; i < detail; i++) {
      let lat = map(i, 0, detail, 0, PI);
      beginShape(TRIANGLE_STRIP);
      for (let j = 0; j <= detail; j++) {
        let lon = map(j, 0, detail, 0, TWO_PI);
        let x = this.r * sin(lat) * cos(lon);
        let y = this.r * sin(lat) * sin(lon);
        let z = this.r * cos(lat);
        let hueValue = map(lat, 0, PI, this.hue, this.hue + 180) % 360; // Adjust the hue range as needed
        fill(hueValue, 100, 100);
        vertex(x, y, z);
        x = this.r * sin(lat + PI / detail) * cos(lon);
        y = this.r * sin(lat + PI / detail) * sin(lon);
        z = this.r * cos(lat + PI / detail);
        vertex(x, y, z);
      }
      endShape();
    }
    pop();
  }  

  update() {
    this.pos.add(this.vel);
    this.handleCollisions();
  
    // Update the hues for the bruises
    for (let bruise of this.bruises) {
      bruise.update();
    }
  
    this.renderBruises();
    this.renderLines();
  }
  

  handleCollisions() {
    let bruise = false;
    let bruisePos = createVector(0, 0, 0);
    let face; // To keep track of the face that was hit
  
    // Handling the collision on the x-axis
    if (this.pos.x + this.r > cubeSize / 2) {
      this.vel.x *= -1;
      this.pos.x = cubeSize / 2 - this.r; // Adjust position
      bruisePos.set(cubeSize / 2, this.pos.y, this.pos.z); // Adjusted position
      face = "x"; // Set face to x
      bruise = true;
    } else if (this.pos.x - this.r < -cubeSize / 2) {
      this.vel.x *= -1;
      this.pos.x = -cubeSize / 2 + this.r; // Adjust position
      bruisePos.set(-cubeSize / 2, this.pos.y, this.pos.z); // Adjusted position
      face = "x"; // Set face to x
      bruise = true;
    }
  
    // Handling the collision on the y-axis
    if (this.pos.y + this.r > cubeSize / 2) {
      this.vel.y *= -1;
      this.pos.y = cubeSize / 2 - this.r; // Adjust position
      bruisePos.set(this.pos.x, cubeSize / 2, this.pos.z); // Adjusted position
      face = "y"; // Set face to y
      bruise = true;
    } else if (this.pos.y - this.r < -cubeSize / 2) {
      this.vel.y *= -1;
      this.pos.y = -cubeSize / 2 + this.r; // Adjust position
      bruisePos.set(this.pos.x, -cubeSize / 2, this.pos.z); // Adjusted position
      face = "y"; // Set face to y
      bruise = true;
    }
  
    // Handling the collision on the z-axis
    if (this.pos.z + this.r > cubeSize / 2) {
      this.vel.z *= -1;
      this.pos.z = cubeSize / 2 - this.r; // Adjust position
      bruisePos.set(this.pos.x, this.pos.y, cubeSize / 2); // Adjusted position
      face = "z"; // Set face to z
      bruise = true;
    } else if (this.pos.z - this.r < -cubeSize / 2) {
      this.vel.z *= -1;
      this.pos.z = -cubeSize / 2 + this.r; // Adjust position
      bruisePos.set(this.pos.x, this.pos.y, -cubeSize / 2); // Adjusted position
      face = "z"; // Set face to z
      bruise = true;
    }

    if (bruise) {
      cubeHue = (cubeHue + 60) % 360; // Change the cube hue upon impact
      switch (face) {
        case "x":
          cameraRotationY += rotationSpeed;
          break;
        case "y":
