      this.bruises.push(new Bruise(bruisePos, this.r, face));
      this.bruiseCount++;
      if (this.bruiseCount > 3) {
        this.removeOldestBruise();
      }
    }
  }

  removeOldestBruise() {
    this.bruises.shift();
    this.bruiseCount--;
