function particlesGenerator(limit, size) {
  var x = geCoordinates()[0];
  var y = geCoordinates()[1];

  for (var i = 0; i < limit; i++) {
    ellipse(x, y, size, size);

    var noiseX = (noise(x/100, y/100, i/100) - 0.5);
    var noiseY = (noise(y/100, i/100, x/100) - 0.5);

    x += 5 * noiseX;
    y += 5 * noiseY;
  }
}

function cloudGenerator() {
  var x = geCoordinates()[0];
  var y = geCoordinates()[1];
  fillWhite(25);
  ellipse(x, y, 50, 50);
}

function pictureFrame() {
  var frameWidth = width-100;
  var frameHeight = height-100;
  noFill();
  stroke(255);
  rect(50, 50, frameWidth, frameHeight);
  stroke(255);
  rect(40, 40, frameWidth, frameHeight);
  stroke(255);
  rect(30, 30, frameWidth, frameHeight);
}

function fillWhite(alph) {
  fill(R.random_num(0, 255), alph);
}

function geCoordinates() {
  var x = R.random_num(width/5, width*4/5);
  var y = R.random_num(height/5, height*4/5);
  return [x, y];
}

