function myObjects(img, idx, cellSize) {
  let rnd = R.random_dec();
  let s = cellSize * R.random_int(2, 6);
 img.noFill();

  img.push();
  img.translate(img.width / 2, img.height / 2);
  img.rotate(45 * random([0, 1]));
  if (idx < 1) {
    img.noStroke();
    img.fill(random(additionalColors.concat(palette1).concat(monetteColors)));
  } else {
    img.strokeWeight(sw * random([1, 2]));
    img.stroke(random(additionalColors.concat(palette2).concat(monetteColors)));
  }

  if (rnd < .3) {
    img.rect(-s / 2, -s / 2, s);
  } else if (rnd < .6) {
    img.circle(0, 0, s);
  } else {
    img.noStroke();
    img.fill(random(additionalColors.concat(palette1).concat(monetteColors)));
    objectCircle(img, s, cellSize);
  }
  img.pop();
  }

function objectCircle(img, s, cellSize) {
  let r = s / 2;
  let ss = cellSize / R.random_choice([1, 2]);
  let seg = R.random_choice([4, 8]);
  let rnd = R.random_dec();
 for (let i = 0; i < seg; i++){
    img.push();
    img.rotate(360 / seg * i);
    if (rnd < .33) {
      img.triangle(r, r, r + ss, r, r, r + ss);
    } else if (rnd < .66) {
      img.rect(r - ss / 2, -ss / 2, ss);
    } else {
      img.circle(r, 0, ss);
    }
    img.pop();
  }}

