function generate_background_texture_line(y_div) {
  var division = 20
  let background_line = []
  for ( let i = 0; i < 1080; i = i + 1080 / division) {
    background_line.push(new Point(i, y_div))
  }
  return catmullRomFitting(background_line, 1, 10)
}


function calcSizes(box_number) {
  let sizes = []

  if(R.random_dec() < .1) {
    box_number = 70
    boxes = true
    for(let i = 0; i < box_number; i++){
      sizes.push("tiny")
    }
  } else {
    for (let i = 0; i < box_number; i++){
      let weighted_choice =  R.random_dec()
      if(weighted_choice < 1/16){
        sizes.push("medium")
      } else if(weighted_choice < 1/4){
        sizes.push("small")
      } else if(weighted_choice < 1/3) {
        sizes.push("large")
      } else {
        sizes.push("tiny")
      }
  
    }
  }
  return sizes
}

function calculateFeatures(tokenData) {
  /**
   * Implement me. This function should return a set of features in the format of key-value pair notation.
   *
   * For example, this should return `{"Palette": "Rosy", "Scale": "Big", "Tilt": 72}` if the desired features for a mint were:
   * - Palette: Rosy
   * - Scale: Big
   * - Tilt: 72
   */
  let hash = tokenData.hash;
  let box_number = R.random_int(25, 45)

  let hair_type = "braid"
  let tragic = false

  if(R.random_dec() < .2) {
    hair_type = "loc"
  }
  if(R.random_dec() < .02) {
    tragic = true
  }
  

  let pallet_choice = get_pallettes()

  return {"box_count": box_number, "pallette": R.random_choice(pallet_choice) , "hair" : hair_type, "undoing": tragic}
}

