class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
} // derive randomness from token hash
let R = new Random();
const seed = R.random_dec()*9999999;

class Points {
    constructor(rows, columns, wid, hei) {
        this.rows = rows;
        this.columns = columns;
        this.width = wid;
        this.height = hei;
        this.x = wid/columns;
        this.y = hei/rows;
        this.gridPoints = this.gridPoints();
    }

    // given rows, columns, width, and height, return an array of points, that form a grid
    gridPoints() {
        let points = [];
        for (let row = 0; row <= this.rows; row++) {
            for (let col = 0; col <= this.columns; col++) {
                let xx = this.x * col;
                let yy = this.y * row;
                let pt = new Point(xx, yy, col, row)
                points.push(pt);
            }
        }
        return points;
    }

    // given a column, row, and array of points, return a point
    getPoint(co, ro) {
        for (let i of this.gridPoints) {
            if ((i.col == co) && (i.row == ro)) {
                return (i)
            }
        }
    }
} // grid

class Point {   
    constructor(x, y, col, row) {
        this.x = x;
        this.y = y;
        this.col = col;
        this.row = row;
    }

    display(clr, maxCanv) {
        strokeWeight(0);
        let d = random(1, 6)*maxCanv*0.0015;
        let w = color(clr);
        w.setAlpha(120);
        fill(w)
        circle(this.x, this.y, d)
    }
}  // given (x, y, col, row), draw a circle


