  for (let e of circlePoints) {
        clr = random(pSet);
        e.display(clr, maxCanv);
    }
  
  // lines
  for (v = 0; v < 3; v++) {
       for (const [key, pt0] of borderPts.entries()) {
          strokeWeight(1*maxCanv*0.002)
          stroke(random(pSet))
          pt1 = secondPoint(pt0)
          if (!intersection(pt0, pt1, circlePoints)) {
               line(pt0.x, pt0.y, pt1.x, pt1.y)
          }
        }
  }  // given two border points and a circle point, draw a line between both border points as long as that line doesn't intersect the circle point
  
  // more lines
  for (v = 0; v < 100; v++) {
       
       let t = random();
       if (t < 0.5) {
         t = 1;
       } else if (t < 0.7) {
         t = 2;
       } else if (t < 0.9) {
         t = 3;
       } else {
         t = 5;
       }
       strokeWeight(t*maxCanv*0.002)
       stroke(random(pSet))
       pt0 = random(borderPts);
       pt1 = secondPoint(pt0)
       if (!intersection(pt0, pt1, circlePoints)) {
            line(pt0.x, pt0.y, pt1.x, pt1.y)
          }
        }
  
  // border
  strokeWeight(maxCanv*0.035);
  stroke(fColour);
  noFill();
  rect(0, 0, width, height)
  
}

class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}


