function mySquare(x, y, w) {
  let minWidth = width / R.random_choice(toby); // Using R.random_choice(toby)
  let probability = map(w, width, minWidth, 1, R.random_choice(skeptic)); // Using R.random_choice(skeptic)
  if (R.random_dec() < probability && w > minWidth) {
    let split = w == width ? 4 : int(R.random_choice([2, 4])); // Using R.random_choice([2, 4])
    let ww = w / split;

    for (let i = 0; i < split; i++) {
      for (let j = 0; j < split; j++) {
        mySquare(x + i * ww, y + j * ww, ww);
      }
    }
  } else {
    let randomColor = R.random_choice(currentPalette.colors); // Using R.random_choice(currentPalette.colors)
    fill(randomColor);
    square(x, y, w);
  }
}



function granulateFuzzify(amount) {
  loadPixels();
  const d = pixelDensity();
  const fuzzyPixels = 2;
  const modC = 4 * fuzzyPixels;
  const modW = 4 * width * d;
  const pixelsCount = modW * (height * d);
  for (let i = 0; i < pixelsCount; i += 4) {
    const f = modC + modW;
    if (pixels[i + f]) {
      pixels[i] = round((pixels[i] + pixels[i + f]) / 2);
      pixels[i + 1] = round((pixels[i + 1] + pixels[i + f + 1]) / 2);
      pixels[i + 2] = round((pixels[i + 2] + pixels[i + f + 2]) / 2);
    }
    pixels[i] = pixels[i] + R.random_num(-amount, amount);
    pixels[i + 1] = pixels[i + 1] + R.random_num(-amount, amount);
    pixels[i + 2] = pixels[i + 2] + R.random_num(-amount, amount);
  }
  updatePixels();
}

