// Prohibition Random 
class ProhibitionRandom {
    constructor() {
      this.useA = false;
      let sfc32 = function (uint128Hex) {
        let a = parseInt(uint128Hex.substring(0, 8), 16);
        let b = parseInt(uint128Hex.substring(8, 16), 16);
        let c = parseInt(uint128Hex.substring(16, 24), 16);
        let d = parseInt(uint128Hex.substring(24, 32), 16);
        return function () {
          a |= 0;
          b |= 0;
          c |= 0;
          d |= 0;
          let t = (((a + b) | 0) + d) | 0;
          d = (d + 1) | 0;
          a = b ^ (b >>> 9);
          b = (c + (c << 3)) | 0;
          c = (c << 21) | (c >>> 11);
          c = (c + t) | 0;
          return (t >>> 0) / 4294967296;
        };
      };
      // seed prngA with first half of tokenData.hash
      this.prngA = new sfc32(tokenData.hash.substring(2, 34));
      // seed prngB with second half of tokenData.hash
      this.prngB = new sfc32(tokenData.hash.substring(34, 66));
      for (let i = 0; i < 1e6; i += 2) {
        this.prngA();
        this.prngB();
      }
    }
    // random number between 0 (inclusive) and 1 (exclusive)
    random_dec() {
      this.useA = !this.useA;
      return this.useA ? this.prngA() : this.prngB();
    }
    // random number between a (inclusive) and b (exclusive)
    random_num(a, b) {
      return a + (b - a) * this.random_dec();
    }
    // random integer between a (inclusive) and b (inclusive)
    // requires a < b for proper probability distribution
    random_int(a, b) {
      return Math.floor(this.random_num(a, b + 1));
    }
    // random boolean with p as percent liklihood of true
    random_bool(p) {
      return this.random_dec() < p;
    }
    // random value in an array of items
    random_choice(list) {
      return list[this.random_int(0, list.length - 1)];
    }
}
  


 /**/ 
 // Random
 class RandomClass {
    constructor() {
        this.randomFn = Math.random;
        this.floorFn = Math.floor; 

        // for debugging purposes 
        this.useProhibition = true; 
        // prohibition's random 
        this.prandom = new ProhibitionRandom(); 
    }
  
    randomDec() {
        if (this.useProhibition) {
            return this.prandom.random_dec(); 
        } else {
            return this.randomFn(); 
        }
    }
  
    randomNum(value) {
        if (this.useProhibition) {
            return this.prandom.random_num(0, value); 
        } else {
            return (this.randomFn() * value);
        }
    }
  
    range(min, max) {
        
        if (this.useProhibition) {
            return this.prandom.random_num(min, max); 
        } else {
            return min + (this.randomFn() * (max - min)); 
        }
    }
  
    list(list) {
        
        if (this.useProhibition) {
            return this.prandom.random_choice(list); 
        } else {
            return list[this.floorFn((this.randomFn() * list.length))];     
        }
    }
  
    weightedRandom(prob) {
        let i;
        let sum=0;  
        let r;
        if (this.useProhibition) {
            r = this.prandom.random_dec(); 
        } else {
            r = this.randomDec(); 
        }
        for (i in prob) {
            sum += prob[i];
            if (r <= sum) return i;
        }
    }
  }




