function customRandomSeed(seed) {
  let generator = function() {
    seed = (seed * 9301 + 49297) % 233280;
    return seed / 233280;
  };

  return function(min_, max_) {
    if (max_ === undefined) {
      max_ = min_;
      min_ = 0;
    }
    return min + generator() * (max - min);
  };
}

let customRandom = customRandomSeed(seed);

