  // Value class
  class Value {
    constructor (start, end, inc) {
        this.start = start;
        this.end = end;
        this.inc = inc;
        this.value = 0;
    }
  
    add() {
        if (this.value < this.end) {
            this.value += this.inc;
        } else {
            this.value = this.start;
        }     
    }
  
    sub() {
        if (this.value > 0) {
            this.value -= this.inc;
        } else {
            this.value = this.end;
        }
    }
  
  
    print() {
        console.log(this.value);
    }
  
  }
  
  /**/
  // All gradient work
  class Gradient {
    constructor (colors, dVariable, dmin, dmax) {
      this.colors = colors;
      this.d = dVariable;
      this.min = dmin;
      this.max = dmax;
      this.fillColor = ""; 
      this.n = this.colors.length;
    }
    
    /**
     * Gradient::pickColor ()
     * @param empty
     * picks color based on mapping based on dVariable (which can be along width or height)
     */ 
    pickColor() {
        // GRADIENT_FLIP -> global
        var index = 0;
        if (GRADIENT_FLIP) {
        index = floor(map(this.d / this.max, 0, 1,  this.n - 1, 0));
        index = constrain(index, 0, this.n - 1);
        this.fillColor = this.colors[index];
      } else {
        index = floor(map(this.d / this.max, 0, 1,  this.n - 1, 0));
        index = constrain(index, 0, this.n - 1);
        this.fillColor = this.colors[index];
      }
      return this.fillColor;
    }
    
  }

  
