// generate random weights 
function generateWeights(n) {
    let _random = new RandomClass(); 
    let weights = {};
    let total = 0;
    // Generate n random numbers between 0 and 1
    let values = Array.from({length: n}, () => _random.randomDec());
    // Sort the values in ascending order
    values.sort((a, b) => a - b);
    // Assign weights proportional to the values
    for (let i = 0; i < n; i++) {
      let weight = i == n - 1 ? 1 - total : values[i+1] - values[i];
      weights[i] = weight;
      total += weight;
    }
    return weights;
  }
  

// old one
// =============

function weightedRandom(prob) {
    let i; 
    let sum=0; 
    let r = customRandom(0, 1); 
    for (i in prob) {
      sum += prob[i];
      if (r <= sum) return i; 
    }
}

function constrainValue(value, min, max) {
    if (value < min) {
        return min;
    } else if (value > max) {
        return max;
    } else {
        return value;
    }
}



















