// ==============================================================================================
// BACKGROUND FUNCTIONALITIES
// ==============================================================================================
let h1, h, s1, s, b1, b, i, j,h2;
let p = 1;
let mv = 50;
let colmv = 2;
let alph = 5;
let maxVary;  
let bg_random = new RandomClass(); 

// controlling the intesity of the background texture
// ----------------------------------------------------------
/* 
NOTE
=====
It's all about distorting the H, S, B values by a MIN and MAX value. 

Example:
=========
"HUE_MIN = -5"  means the hue of given background color, is going to be subtracted by 5 at some points in the canvas when the background texture is drawn ("HUE_MAX = +5" is vice versa). 
The range of -5 and +5, is the key. 
Bigger ranges can be very chaotic (like -10 and +20)

*/
// All implemented below:


// for HUE 
let HUE_MIN = -10; 
let HUE_MAX = 10; 


// for SATURATION
let SAT_MIN = -10; 
let SAT_MAX = 10; 


// for BRIGHTNESS
let BR_MIN = -15; 
let BR_MAX = 15; 


// ----------------------------------------------------------

function getColor(bg){
 
  // h1 = h = random(360); 
  h1 = h = bg.hue; // * 360 
    hMax = constrain(h + HUE_MAX, 0, 360); 
    hMin = constrain(h + HUE_MIN, 0, 360); 

  // s1 = s = 90; 
  s1 = s = bg.saturation; // * 120 
    sMax = constrain(s + SAT_MAX, 0, 120); 
    sMin = constrain(s + SAT_MIN, 0, 120);
  
  // b1 = b = 70; 
  b1 = b = bg.brightness; // * 100 
    bMax = constrain(b + BR_MAX, 0, 100);  
    bMin = constrain(b + BR_MIN, 0, 100); 
  
    i = bg_random.randomNum(width);
    j = bg_random.randomNum(height); 
  }
  
  function fillBackground() {
      for (q = 0; q < numb; q++) {
        i = i + bg_random.range(-mv, mv);
        j = j + bg_random.range(-mv, mv);
        h = h + bg_random.range(-colmv, colmv);
        s = s + bg_random.range(-colmv*2, colmv*2);
        b = b + bg_random.range(-colmv*2, colmv*2);
        if (i < 0) {
          i = width;
        }
        else if (i > width) {
          i = 0;
        }
        if (j < 0) {
          j = height;
        }
        else if (j > height) {
          j = 0;
        }

    if (h > hMax) {
          h = hMax;
        }
        else if (h < hMin) {
          h = hMin;
        }
        h2=h;
        if (h > 360) {
          h2 = h - 360;
        }
        else if (h < 0) {
          h2 = h + 360;
        }
        if (s > sMax) {
          s = sMax;
        }
        if (s < sMin) {
          s = sMin;
        }
        if (b > bMax) {
          b = bMax;
        }
        else if (b < bMin) {
          b = bMin;
        }
        fill(h2, s, b, alph);
        push();
        translate(i, j);
        rotate(bg_random.randomNum(PI * 2));
        beginShape();
        for (m = 0; m < PI * 2; m += 1) {
          r = bg_random.range(20, 50);
          let x = cos(m) * r;
          let y = sin(m) * r;
          vertex(x, y);
        }
        endShape(CLOSE);
        pop();
      }
  }
  
  function paperTexture(){
    noFill();
    textureNum = width*height/50;
    for(i=0;i<textureNum;i++){
      stroke(bg_random.range(100,150),bg_random.range(100,150),bg_random.range(100,150),6);
      x = bg_random.range(-width*0.2, width*1.2);
      y = bg_random.range(-height*0.2, height*1.2);
      push();
      translate(x,y);
      strokeWeight(3);
      point(0,0);
      strokeWeight(1);
      rotate(bg_random.randomNum(PI*2));
      curve(bg_random.range(60,220),0,0,bg_random.range(-50,50),bg_random.range(-50,50),bg_random.range(60,120),bg_random.range(60,120),bg_random.range(60,220));
      pop();
    }
  }


  function hexToHSB(hexColor) {
    // Remove the "#" symbol if present
    if (hexColor.startsWith("#")) {
      hexColor = hexColor.substring(1);
    }
  
    // Parse the hex color components (RGB values)
    const r = parseInt(hexColor.substring(0, 2), 16);
    const g = parseInt(hexColor.substring(2, 4), 16);
    const b = parseInt(hexColor.substring(4, 6), 16);
  
    // Convert RGB to HSB
    const max = Math.max(r, g, b);
    const min = Math.min(r, g, b);
    const delta = max - min;
  
    let hue, saturation, brightness;
    if (delta === 0) {
      hue = 0;
    } else if (max === r) {
      hue = (g - b) / delta + (g < b ? 6 : 0);
    } else if (max === g) {
      hue = (b - r) / delta + 2;
    } else {
      hue = (r - g) / delta + 4;
    }
    hue *= 60;
    if (hue < 0) {
      hue += 360;
    }
  
    brightness = max / 255;
    saturation = max === 0 ? 0 : delta / max;
  
    // Convert HSB values to match p5.js colorMode(HSB, 360, 120, 100, 255)
    hue = Math.round(hue);
    saturation = Math.round(saturation * 120);
    brightness = Math.round(brightness * 100);
  
    return { hue, saturation, brightness };
  }
  

  



















