function isValid(c) { 
  /*
    false - means it will fucking collide
    true - we're good to go
  */
  
  // special tricks
  if (c.r > max_) {
    return false; 
  } 
  
  // frame check 
  if ( (c.x - c.r) < framewidth ||
      (c.x + c.r) > width-framewidth ) {
    return false; 
  }
  if ( (c.y - c.r) < framewidth ||
      (c.y + c.r) > height-framewidth ) {
    return false; 
  }
  
  
  // collission check 
  // var threshold = 0.65; // should be >= 0.8 (but doesn't hurt to try extremes) 
  threshold = THRESHOLD_VALUE; 
  for (var i = 0; i < circles.length; ++i) {
    var c2 = circles[i]; 
    var dx = c2.x - c.x; 
    var dy = c2.y - c.y; 
    var dist_ = sqrt(dx * dx + dy * dy);   
    if (dist_ < (c2.r + c.r) * threshold ) {
      return false; 
    }
  }
  
  
  // if we got here then it's all good
  return true; 
}


function createCircle() { 
  return {
    x: _random.range(framewidth, width - framewidth), 
    y: _random.range(framewidth, height - framewidth), 
    r: min_
  }
}


function drawCircle(c) { 
  // special tricks
  // if (c.r > max_ * 0.8) return; 
  
  // draw element 
  if (!allow_doodle) {
    // push to array
    circles.push(c); 
    // draw 
    fill(_random.list(palette.palette)); 
    ellipse(c.x, c.y, c.r*2, c.r*2); 
  } 
  else {   
    var doodle_color = _random.list(palette.palette); 
      // with a fancy coloring technique (eg weighted random) 
      doodle_color = COLORING_TECHNIQUE.apply(c.x, c.y); 

      // var revolutions = map(c.r, min_, max_, 3, 15); 
      var revolutions = _random.range(5, 15);  
    var d = new Doodle(c.x, c.y, doodle_color, revolutions); 
    // push to array
    circles.push(c); // push this too and thank me later
    doodles.push(d); 
    // set doodle radius
    // d.startRadius = c.r * 0.8; 
    d.startRadius = c.r; 
    // set doodle shadow color
    if (palette.shadow.length > 0) { // if not empty
      d.shadowCol = _random.list(palette.shadow); 
          // with a fancy coloring technique (eg weighted random) 
          // d.shadowCol = COLORING_TECHNIQUE.apply(c.x, c.y);  
          
    } 
    d.display(); 

        if (debug_doodle) {
          fill("#ffffff00")
          stroke("lime"); 
          ellipse(c.x, c.y, c.r * 2, c.r * 2); 
        }
  }
  
}


