class Doodle {
    /*
          x    ->   x coordinate (float)
          y    ->   y coordinate (float)
          col  ->   color (string)
          rev  ->   no of revolutions (integer)
    */
    constructor(x, y, col, rev) { 
        this.x = x; 
        this.y = y; 
        // this.startRadius = random(3, 10) * scale_; 
        this.startRadius = 0; 
        this.centerx = this.x; 
        this.centery = this.y; 
        this.angle = _random.range(1, 20); 
        this.inc = 0; 
        // this.size = random(1, 3) * scale_; 
        this.size = _random.range(10, 15); //line weight
        this.direction = _random.randomDec(); 
        this.col = col; 
        // this.col = "#007EFF"; 

        // shadow
        this.shadowCol = "#0f1116"; // #0f1116 by default (if not set) 
        // this.shadowCol = "#bc9331"; 
        this.shadowOff = 5; 
        this.shadowSize = this.size * 0.3; // * scale value 
        
        this.distortx = 0; 
        this.distorty = 0; 
        this.count = 0; 
        this.displayCount = 0; 
 
        this.revolutions = rev; 
        // this.revolutions = floor(_random.range(3, 8)); 
        /*
        1 revolution  =  360 degrees (TWP_PI)
        n revolutions =  [TWO_PI * n] degrees
        */
        this.totalangle = this.revolutions * TWO_PI; 

        this.canvas = null; 
        this.inc_val = 0.05; 
    } 

    
    drawDoodle() {
        if (this.canvas != null) { 
            this.canvas.noStroke(); 
            this.canvas.fill(this.col); 
            this.canvas.ellipse(this.x, this.y, this.size, this.size); 
            if (allow_shadow) {
              this.canvas.fill(this.shadowCol); 
              this.canvas.ellipse(this.x + this.shadowOff, this.y + this.shadowOff, this.size, this.size); 
            }
        } else {
            noStroke(); 
            fill(this.col); 
            ellipse(this.x, this.y, this.size, this.size); 
            if (allow_shadow) {
              fill(this.shadowCol); 
              ellipse(this.x + this.shadowOff, this.y + this.shadowOff, this.shadowSize, this.shadowSize); 
            }
        }
    }
   
     
    display() {
      // this.startRadius *= scale_; 
      
      // Set a limit to prevent infinite recursion
      if (this.displayCount >= 4000) {
        return;
      }

      if (abs(this.angle) >= this.totalangle) {
        return; 
      }

      // Increment the displayCount
      this.displayCount++;
      
      if (this.direction < 0.5) {
      this.angle += 0.01; 
      } else {
          this.angle -= 0.01; 
      }

      var xscale = 0.0001; 
      var yscale = 0.0003; 
      // xscale = 0.001; yscale = 0.0002;
  
      this.inc -= (this.inc_val * noise(this.x * xscale, this.angle / yscale)); 
      this.distortx = noise(this.x * xscale, this.angle * 0.5); 
      this.distorty = noise(this.y * yscale, this.angle * 0.5); 
      var distortedLength = (this.startRadius + this.inc); 
      distortedLength = constrain(distortedLength, 0, this.startRadius - 13);  
      this.x = this.centerx + cos(this.angle + this.distortx) * distortedLength;  
      this.y = this.centery + sin(this.angle + this.distorty) * distortedLength; 

      if (this.x != 0 && this.y != 0) {
          this.drawDoodle(); 
      }
      // call recursively
      this.display();  
    }
    
  }

class Random {
  constructor() {
    this.useA = false;
    let sfc32 = function (uint128Hex) {
      let a = parseInt(uint128Hex.substring(0, 8), 16);
      let b = parseInt(uint128Hex.substring(8, 16), 16);
      let c = parseInt(uint128Hex.substring(16, 24), 16);
      let d = parseInt(uint128Hex.substring(24, 32), 16);
      return function () {
        a |= 0;
        b |= 0;
        c |= 0;
        d |= 0;
        let t = (((a + b) | 0) + d) | 0;
        d = (d + 1) | 0;
        a = b ^ (b >>> 9);
        b = (c + (c << 3)) | 0;
        c = (c << 21) | (c >>> 11);
        c = (c + t) | 0;
        return (t >>> 0) / 4294967296;
      };
    };
    // seed prngA with first half of tokenData.hash
    this.prngA = new sfc32(tokenData.hash.substring(2, 34));
    // seed prngB with second half of tokenData.hash
    this.prngB = new sfc32(tokenData.hash.substring(34, 66));
    for (let i = 0; i < 1e6; i += 2) {
      this.prngA();
      this.prngB();
    }
  }
  // random number between 0 (inclusive) and 1 (exclusive)
  random_dec() {
    this.useA = !this.useA;
    return this.useA ? this.prngA() : this.prngB();
  }
  // random number between a (inclusive) and b (exclusive)
  random_num(a, b) {
    return a + (b - a) * this.random_dec();
  }
  // random integer between a (inclusive) and b (inclusive)
  // requires a < b for proper probability distribution
  random_int(a, b) {
    return Math.floor(this.random_num(a, b + 1));
  }
  // random boolean with p as percent liklihood of true
  random_bool(p) {
    return this.random_dec() < p;
  }
  // random value in an array of items
  random_choice(list) {
    return list[this.random_int(0, list.length - 1)];
  }
}


