function setup() {
    randomSeed(seed);
noiseSeed(seed);
  createCanvas(windowWidth, windowHeight);
  background(R.random_num(0, 255), R.random_num(0, 255), R.random_num(0, 255));

  patternsQty = R.random_int(3, 8);
  masksQty = R.random_int(3, 8);
  intersectionsQty = R.random_int(5, 15);

  for (let i = 0; i < patternsQty; i++) {
    const pattern = createGraphics(width, height);
    const col = lerpPalette(palette, R.random_dec());
    const angleOffset = R.random_dec() * PI;
    const angleAmplitude = R.random_dec() * PI * 0.5;
    pattern.background(col);
    pattern.noFill();
    const lineSize = max(width, height);
    for (let i = 0; i < 1000; i++) {
      pattern.stroke(R.random_num(0, 255), R.random_num(20, 50));
      pattern.strokeWeight(R.random_num(1, 4));
      const x = R.random_num(0, width);
      const y = R.random_num(0, height);
      pattern.push();
      pattern.translate(x, y);
      pattern.rotate(angleOffset + R.random_dec() * angleAmplitude);
      pattern.line(-lineSize, 0, lineSize, 0);
      pattern.pop();
    }
    patterns.push(pattern);
  }

  for (let i = 0; i < masksQty; i++) {
    const maskGraphics = createGraphics(width, height);
    maskGraphics.noStroke();
    const x = R.random_num(0.2, 0.8) * width;
    const y = R.random_num(0.2, 0.8) * height;
    const diam = R.random_num(0.4, 0.6) * width;
    circles.push({ x, y, diam });
    maskGraphics.circle(x, y, diam);
    masks.push(maskGraphics);
  }

  for (let i = 0; i < intersectionsQty; i++) {
    const mask1 = R.random_choice(masks);
    const mask2 = R.random_choice(masks.filter((a, b) => a != b));
    const intersection = applyMask(mask1, mask2);
    image(applyMask(R.random_choice(patterns), intersection), 0, 0);
  }

  granulateFuzzify(70);

  noFill();
  stroke(0);
  for (let c of circles) {
    strokeWeight(R.random_num(3, 15));
    circle(c.x, c.y, c.diam);
  }
}

function granulateFuzzify(amount) {
  loadPixels();
  const d = pixelDensity();
  const fuzzyPixels = 2;
  const modC = 4 * fuzzyPixels;
  const modW = 4 * width * d;
  const pixelsCount = modW * (height * d);
  for (let i = 0; i < pixelsCount; i += 4) {
    const f = modC + modW;
    if (pixels[i + f]) {
      pixels[i] = round((pixels[i] + pixels[i + f]) / 2);
      pixels[i + 1] = round((pixels[i + 1] + pixels[i + f + 1]) / 2);
      pixels[i + 2] = round((pixels[i + 2] + pixels[i + f + 2]) / 2);
    }
    pixels[i] = pixels[i] + random(-amount, amount);
    pixels[i + 1] = pixels[i + 1] + random(-amount, amount);
    pixels[i + 2] = pixels[i + 2] + random(-amount, amount);
  }
  updatePixels();
}

function applyMask(img, mask) {
  const bufferMask = createGraphics(mask.width, mask.height);
  bufferMask.background(255);
  bufferMask.blendMode(REMOVE);
  bufferMask.image(mask, 0, 0);

  const bufferImg = createGraphics(img.width, img.height);
  bufferImg.image(img, 0, 0);
  bufferImg.blendMode(REMOVE);
  bufferImg.image(bufferMask, 0, 0);
  bufferImg.blendMode(BLEND);

  return bufferImg;
}

function lerpPalette(palette, i) {
  const j = floor(i * palette.length);
  const k = fract(i * palette.length);
  return lerpColor(color(palette[j]), color(palette[(j + 1) % palette.length]), k);
}

