function draw() {
  palette = R.random_choice(palettes); // Select a palette based on the seed
  background(50);
  grid = setGrid();
  customShuffle(grid);
  for (let g of grid) {
    let r = SIZE * (MIN_SCALE - 1) + R.random_num(0, SIZE * (MAX_SCALE - 1));
    let rot = (MAX_TILT) * (R.random_dec() - 0.5);
    push();
    translate(g.x, g.y);
    push();
    drawingContext.shadowOffsetX = SHADOW[0];
    drawingContext.shadowOffsetY = SHADOW[1];
    drawingContext.shadowColor = color(0, SHADOW_INTENSITY * 255);
    drawingContext.shadowBlur = max(SHADOW);
    rotate(rot);
    noStroke();
    rect(0 - (SIZE + r) / 2, 0 - (SIZE + r) / 2, SIZE + r);
    pop();
    rotate(rot);
    let chosenShape = shapes[R.random_int(0, shapes.length - 1)];
    let colors = shuffle(palette); // Shuffle the selected palette for this shape
    chosenShape.shape(0, 0, SIZE + r, colors); // Pass the shuffled colors to the shape
    if (ADD_CONTOUR) {
      push();
      stroke(0);
      noFill();
      strokeWeight(CONTOUR_WEIGHT);
      rectMode(CENTER);
      rect(0, 0, SIZE + r);
      pop();
    }
    pop();
  }
}


function customShuffle(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = R.random_int(0, i);
    [array[i], array[j]] = [array[j], array[i]];
  }
}


function setGrid() {
	let g = [];
	for (let y = SIZE / 2; y < height + SIZE / 2; y += SIZE) {
