function dividedRect(x, y, w, h, n) {
  stroke(100);
  fill(random(selectedGroup)) // Selecting a random color from the group
  beginShape();
  for (let i = 0; i < w; i++) {
    let nX = -15 + 30 * noise((x + i) * 0.01, y * 0.01);
    let nY = -15 + 30 * noise((x + i) * 0.01, -1000 + y * 0.01);
    vertex(x + i + nX, y + nY);
  }
  for (let i = 0; i < h; i++) {
    let nX = -15 + 30 * noise((x + w) * 0.01, (y + i) * 0.01);
    let nY = -15 + 30 * noise((x + w) * 0.01, -1000 + (y + i) * 0.01);
    vertex(x + w + nX, y + i + nY);
  }
  for (let i = w; i > 0; i--) {
    let nX = -15 + 30 * noise((x + i) * 0.01, (y + h) * 0.01);
    let nY = -15 + 30 * noise((x + i) * 0.01, -1000 + (y + h) * 0.01);
    vertex(x + i + nX, y + h + nY);
  }
  for (let i = h; i > 0; i--) {
    let nX = -15 + 30 * noise(x * 0.01, (y + i) * 0.01);
    let nY = -15 + 30 * noise(x * 0.01, -1000 + (y + i) * 0.01);
    vertex(x + nX, y + i + nY);
  }
  endShape();
  n--;
  if (n > 0) {
    if (w >= h) {
      let rW = random(w * 0.1, w * 0.9);
      dividedRect(x + random(10), y + random(10), rW - random(20), h - random(20), n);
      dividedRect(x + rW + random(10), y + random(10), w - rW - random(20), h - random(20), n);
    } else {
      let rH = random(h * 0.1, h * 0.9);
      dividedRect(x + random(10), y + random(10), w - random(20), rH - random(20), n);
      dividedRect(x + random(10), y + rH + random(10), w - random(20), h - rH - random(20), n);
    }
  }}

