        rotate(0);
      } else {
        rotate(90);
      }
      dTri(0, 0, size - margin, 4);
      rotate(180);
      dTri(0, 0, size - margin, 4);
      pop();
    }
  }
}

function dTri(x, y, s, n) {
  let hs = s / 2;
  push();
  translate(x, y);
  stroke(0);
  if (R.random_bool(0.5)) {
    fill(0);
  } else {
    fill(255);
  }

  triangle(-hs, -hs, hs, -hs, -hs, hs);
  pop();
  n--;

  if (n >= 0) {
    if (R.random_bool(0.5)) {
      push();
      translate(x, y - hs);
      rotate(-135);
      dTri(0, 0, s * sqrt(0.5), n);
      pop();
    }

    if (R.random_bool(0.5)) {
      push();
      translate(x - hs, y);
      rotate(135);
      dTri(0, 0, s * sqrt(0.5), n);
      pop();
    }
  }
}

