  // image(overallTexture, 0, 0); // Corrected variable name
  pop();
}
class Particle {
  constructor(args) {
    let def = {
      p: createVector(0, 0),
      v: createVector(0, 0),
      a: createVector(0, 0),
      r: 10,
      dp: R.random_num(0.93, 0.99), // Replace with deterministic randomness
      angMult: R.random_num(10, 50), // Replace with deterministic randomness
      color: R.random_choice(colors),
    };
    Object.assign(def, args);
    Object.assign(this, def);
  }
draw() {
  mainGraphics.push();
  mainGraphics.translate(this.p.x, this.p.y);
  mainGraphics.fill(this.color);
  mainGraphics.noStroke();
  mainGraphics.ellipse(0, 0, this.r);
  mainGraphics.pop();
}

update() {
  this.p.add(this.v);
  this.a.x = (noise(this.p.x, this.p.y, 5) - 0.5) * 1.1;
  this.a.y = (noise(this.p.x, this.p.y) - 0.5) * 1.1;
  this.v.add(this.a);
  
  let delta = createVector(this.p.x - width / 2, this.p.y - height / 2);
  let ang = map(noise(this.p.x, this.p.y), 0, 1, -PI, PI);
  let rr = delta.mag();

  this.v.x += -sin(ang * this.angMult + rr / 5) / 15 + cos(rr / 10) / 10;
  this.v.y += -cos(ang * this.angMult + rr / 5) / 15 + sin(rr / 10) / 10;
  
  this.v.mult(0.95);
  this.r *= this.dp;
}
}

