		if (i % ik == 0) {
			let xk1 = xk * ((pr() < 0.5) ? xk11 : rI(xk1P));
			
			for (let j = 1; j <= n; j++) {
				let xk2 = xk1 * rI([0.97,0.975,0.98,0.985,0.99,0.995,1,1.005,1.01,1.015,1.02,1.025,1.03]);
				
				let x0 = w0 * i + ma;
				let y0 = w0 * j + ma;
				
				for (let k = 1; k <= tl; k++) {
					rot(cw, 0, kk * k / i * rd0);
					
					c = (y0 < ch * cok[0] * rI(ca)) ? colors[0] : (y0 < ch * cok[1] * rI(ca)) ? colors[1] : (y0 < ch * (cok[1] + cokk) * rI(ca)) ? colors[2] : (y0 < ch * cok[2] * rI(ca)) ? colors[3] : (y0 < ch * (cok[2] + cokk) * rI(ca)) ? colors[4] : (y0 < ch * cok[3] * rI(ca)) ? colors[5] : (y0 < ch * (cok[3] + cokk) * rI(ca)) ? colors[6] : (y0 < ch * cok[4] * rI(ca)) ? colors[7] : (y0 < ch * (cok[4] + cokk) * rI(ca)) ? colors[8] : colors[9];
					
					let ka = kxy[1];
					let x = x0 + kxy[2] * xk * k;
					let y = y0 + kxy[3] * xk * k;
					let w = xk2 * k;
					let h = xhk * xk2 * k;
					
					// Shadow
					rectangle(x - 30, y + 100, w, 100, [0,0,0], 0.02);
					rectangle(x, y, 1, 4, [0,0,0], 0.15);
					
					// 1
					rectangle(x, y, w * 1.2, h * 1.2, c, ka);
					
					// 2
					rectangle(x + 3, y + 3, w * 0.25, h * 8, c, ka * 0.28);
					rectangle(x + 2, y + 2, w * 0.2, h * 8, c, ka * 0.28);
					rectangle(x, y, w * 0.15, h * 8, c, ka * 0.28);
					
					// 3
					rectangle(x + 3, y + 3, w * 0.25, h * 16, c, ka * 0.25);
					rectangle(x + 2, y + 2, w * 0.15, h * 16, c, ka * 0.25);
					rectangle(x, y, w * 0.1, h * 16, c, ka * 0.25);
					
					// 4
					rectangle(x + 3, y + 3, w * 0.35, h * 12, c, ka * 0.3);
					rectangle(x + 2, y + 2, w * 0.3, h * 12, c, ka * 0.3);
					rectangle(x, y, w * 0.25, h * 12, c, ka * 0.3);
					
					// 5
					rectangle(x, y, w * 12, h * 0.4, c, ka);
				}
			}
		}
	}
	
	ctx.setTransform(scale,0,0,scale,0,0);
	
	// Filter
	var img = ctx.getImageData(0,0,canvas.width,canvas.height);
	var data = img.data;
	
	var fil = coa[2];
	var balance = { red: fil[0], green: fil[1], blue: fil[2] }, contrast = fil[3], brightness = fil[4];
	
	for (var i = 0; i < data.length; i += 4) {
		data[i] += balance.red;
		data[i + 1] += balance.green;
		data[i + 2] += balance.blue;
		
		data[i] = (data[i] - 128) * contrast + 128;
		data[i + 1] = (data[i + 1] - 128) * contrast + 128;
		data[i + 2] = (data[i + 2] - 128) * contrast + 128;
		
		data[i] += brightness;
		data[i + 1] += brightness;
		data[i + 2] += brightness;
	}
	
	ctx.putImageData(img,0,0);
	
	// Grain
	var no = 128;
	var ta = 25;
	var gap = 10;
	var roa = rN(0, 7) * 45;
	
	ctx.globalAlpha = 0.01;
	rot(cw / 2, ch / 2, roa * rd0);
	for (let i = 1; i <= no; i++) {
		for (let j = 1; j <= no; j++) {
			if (pr() < 0.6) {
				let x = gap * i - 320;
				let y = gap * j - 320;
				
				for (let k = 1; k <= ta; k++) {
					ctx.fillStyle = (k % 2 == 0) ? '#000000' : '#'+rI(colors); ctx.fillRect(x + 5 * k, y + k * 5, 0.3 * k, 0.3 * k);
				}
			}
		}
	}
	rot(cw / 2, ch / 2, -roa * rd0);
	ctx.globalAlpha = 1;
	
	var gc = [[0,0,0],[34,34,34],[68,68,68],[102,102,102],[170,170,170]].concat(colors);
	var ga = 0.04;
	for (let i = 0; i <= 18000; i++) {
		let a = rd0 * 10 * rI([0.25,0.5,0.75,1,1.25,1.5,1.75,2]) * rN(-1,1);
		rot(cw / 2, ch / 2, a);
		rectangle(rN(0, cw), rN(0, ch), 1, 18, rI(gc), ga);
		rectangle(rN(0, cw), rN(0, ch), 18, 1, rI(gc), ga);
		ctx.setTransform(scale,0,0,scale,0,0);
	}
	
	for (let i = 1; i <= 500; i++) {
		circle(rN(-cw * 0.5, cw * 1.5), rN(0, ch), rN(10,150), rI(colors), 0.005);
	}
	
	gc = [[34,34,34],[68,68,68],[102,102,102],[136,136,136],[170,170,170]].concat(colors);
	ga = 0.15;
	
	for (let i = 0; i < 18000; i++) {
		rectangle(rN(0, cw), rN(0, ch), 0.5, 4, rI(gc), ga);
		rectangle(rN(0, cw), rN(0, ch), 4, 0.5, rI(gc), ga);
		rectangle(rN(0, cw), rN(0, ch), 0.8, 1.2, rI(gc), ga);
		rectangle(rN(0, cw), rN(0, ch), 1.2, 0.8, rI(gc), ga);
	}
	
	// Frame
	var f1 = cw * 0.022, f2 = cw * 0.018, f3 = cw * 0.014, f4 = cw * 0.006;
	rectangleE(f1 / 2, f1 / 2, cw - f1, ch - f1, f1, colors[1], 0.15);
	rectangleE(f2 / 2, f2 / 2, cw - f2, ch - f2, f2, [0,0,0], 0.15);
	rectangleE(f3 / 2, f3 / 2, cw - f3, ch - f3, f3, toRGB(coa[1]), 0.85);
	rectangleE(f4 / 2, f4 / 2, cw - f4, ch - f4, f4, colors[9], 0.1);
}

