function draw() {
  smooth();

  scale(0.8);
  translate(-WIDTH / 2, -HEIGHT / 2);

  hours = hour();
  minutes = minute();
  seconds = second();

  background(bcol);

  let timeString = nf(hours, 2) + ":" + nf(minutes, 2) + ":" + nf(seconds, 2);

  if (minutes === 0 && seconds % 2 == 0) {
    fill(scol);

    for (let j = 0; j < hr; j++) {
      rect(
        WIDTH / 2,
        (j * HEIGHT) / hr + HEIGHT / hr / 2,
        WIDTH,
        HEIGHT / hr - 15 * M
      );
    }
  }

  for (let j = 0; j < hr; j++) {
    rows[j].anim();
  }

  if (minutes === 0 && minutes !== lastMinute) {
    console.log("It's a new hour!");

    setHour();

    lastMinute = 0;
  } else if (minutes !== lastMinute) {
    lastMinute = minutes;
  }

  if (seconds === 0 && seconds !== lastSecond) {
    setMinute();

    lastSecond = 0;
  } else if (seconds !== lastSecond) {
    lastSecond = seconds;
  }
}

function setHour() {
  if (hours > 12) {
    hr = hours - 12;
    bcol = color(255);
    scol = color(0);
  } else if (hours == 12) {
    hr = hours;
    bcol = color(255);
    scol = color(0);
  } else if (hours == 0) {
    hr = 12;
    bcol = color(0);
    scol = color(255);
  } else {
    hr = hours;
    bcol = color(0);
    scol = color(255);
  }

  rows = [];

  for (let j = 0; j < hr; j++) {
    rows.push(new Row(j));
  }
}

function setMinute() {
  for (let row of rows) {
    if (minutes != 0) {
      row.addmin();
    }
  }
}

class Row {
  constructor(j) {
    this.flock = [];
    this.j = j;

    this.setup();
  }

  setup() {
    this.flock = [];
    for (let i = 0; i < minutes; i++) {
      this.flock.push(new Boid(this.j));
    }
  }

  addmin() {
    this.flock.push(new Boid(this.j));
  }

  anim() {
    for (let boid of this.flock) {
      boid.edges();

      boid.flock(this.flock);
      boid.update();

      boid.show();
    }
  }

  update() {}
}
class Boid {
  constructor(j) {
    this.position = createVector(R.random_int(0,width), 175);
    this.velocity = createVector(1, 0);
    this.ran_velocity = createVector(1, 0);
    this.velocity.setMag(R.random_num(-1 * M, 1 * M));
    this.acceleration = createVector();
    this.maxForce = 0.2;
    this.maxSpeed = 1 * M;
    this.j = j;

    this.rs = R.random_num(0, 1);
    this.rsv = R.random_num(1, 2);
    this.rsvb = R.random_num(10, 100);
  }

  edges() {
    if (this.position.x > WIDTH - 20 * M) {
      this.position.x = 0 + 20 * M;
    } else if (this.position.x <= 0 + 20 * M) {
      this.position.x = WIDTH - 20 * M;
    }
    if (this.position.y > DIM - 20 * M) {
      this.position.y = 0;
    } else if (this.position.y < 0 + 20 * M) {
      this.position.y = height - 20 * M;
    }
  }

  flock(boids) {}

  update() {
    this.position.add(this.velocity);
    this.velocity.add(this.acceleration);
    this.velocity.limit(this.maxSpeed);
  }

  show() {
    if (this.rs <= 0.9) var fsw = this.rsv;
    else var fsw = this.rsvb;

    noStroke();
    fill(scol);
    push();
    translate(this.position.x, this.j * (HEIGHT / hr) + HEIGHT / hr / 2);
    var r = map(
      noise(
        this.position.x / (30 * M),
        this.position.y / (30 * M),
        seconds / 100
      ),
      0,
      1,
      -PI / 30,
      PI / 30
    );
    rotate(r);

    rect(
      0,
      0,
      3 * M + noise(this.j / (100 * M) + this.position.x / (100 * M)) * fsw * M,
      HEIGHT / hr - 15 * M
    );
    pop();
  }
}

class Random {
    constructor() {
        this.useA = false;
        let sfc32 = function (uint128Hex) {
            let a = parseInt(uint128Hex.substring(0, 8), 16);
            let b = parseInt(uint128Hex.substring(8, 16), 16);
            let c = parseInt(uint128Hex.substring(16, 24), 16);
            let d = parseInt(uint128Hex.substring(24, 32), 16);
            return function () {
                a |= 0;
                b |= 0;
                c |= 0;
                d |= 0;
                let t = (((a + b) | 0) + d) | 0;
                d = (d + 1) | 0;
                a = b ^ (b >>> 9);
                b = (c + (c << 3)) | 0;
                c = (c << 21) | (c >>> 11);
                c = (c + t) | 0;
                return (t >>> 0) / 4294967296;
            };
        };
        // seed prngA with first half of tokenData.hash
        this.prngA = new sfc32(tokenData.hash.substring(2, 34));
        // seed prngB with second half of tokenData.hash
        this.prngB = new sfc32(tokenData.hash.substring(34, 66));
        for (let i = 0; i < 1e6; i += 2) {
            this.prngA();
            this.prngB();
        }
    }
    // random number between 0 (inclusive) and 1 (exclusive)
    random_dec() {
        this.useA = !this.useA;
        return this.useA ? this.prngA() : this.prngB();
    }
    // random number between a (inclusive) and b (exclusive)
    random_num(a, b) {
        return a + (b - a) * this.random_dec();
    }
    // random integer between a (inclusive) and b (inclusive)
    // requires a < b for proper probability distribution
    random_int(a, b) {
        return Math.floor(this.random_num(a, b + 1));
    }
    // random boolean with p as percent liklihood of true
    random_bool(p) {
        return this.random_dec() < p;
    }
    // random value in an array of items
    random_choice(list) {
        return list[this.random_int(0, list.length - 1)];
    }
}
