    push();
    fill(col[u % col.length]);
    noStroke();
    strokeWeight(0);
    rect(-cw, y - cw / n, 2 * cw, 2 * cw / n);
    rect(-cw, y2 - cw / n, 2 * cw, 2 * cw / n);
    var y1 = (y / cw - 1 / n);
    var theta01 = undefined;
    var theta00 = undefined;
    var theta10 = undefined;
    var theta11 = undefined;
    if (abs(y1) < r0) {
      theta00 = asin(y1 / r0);
    }
    if (abs(y1) < r1) {
      theta01 = asin(y1 / r1);
    }
    y1 = (y + height / n - cx) / cw;
    var y1 = (y / cw + 1 / n);
    if (abs(y1) < r0) {
      theta10 = asin(y1 / r0);
    }
    if (abs(y1) < r1) {
      theta11 = asin(y1 / r1);
    }
    push();
    fill(col[(u + 1) % col.length]);
    for (var sign = -1; sign < 2; sign += 2) {
      if (theta00 != undefined && theta10 == undefined && theta11 == undefined && theta01 == undefined) {
        beginShape();
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, PI - theta00, theta00);
          vertex(r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
