          vertex(-r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
        }
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, theta01, PI - theta01);
          vertex(-r1 * cw * cos(theta), sign * r1 * cw * sin(theta));
        }
        endShape();
      }
      if (theta00 != undefined && theta10 == undefined && theta11 == undefined && theta01 != undefined) {
        beginShape();
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, PI - theta00, theta00);
          vertex(r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
        }
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, PI - theta01, theta01);
          vertex(-r1 * cw * cos(theta), sign * r1 * cw * sin(theta));
        }
        endShape();
      }
      if (theta00 != undefined && theta10 != undefined && theta11 == undefined && theta01 == undefined) {
        beginShape();
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, theta00, theta10);
          vertex(r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
        }
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, theta10, theta00);
          vertex(-r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
        }
        endShape();
      }
      if (theta00 != undefined && theta10 != undefined && theta11 != undefined && theta01 != undefined) {
        beginShape();
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, theta00, theta10);
          vertex(-r0 * cw * cos(theta), sign * r0 * cw * sin(theta));
        }
        for (var v = 0; v <= details; v++) {
          var theta = map(v, 0, details, theta11, theta01);
          vertex(-r1 * cw * cos(theta), sign * r1 * cw * sin(theta));
        }
        endShape();
        beginShape();
        for (var v = 0; v <= details; v++) {
