      let x = offset + i * (d + margin) + d / 2;
      let y = offset + j * (d + margin) + d / 2;
      push();
      translate(x, y);
      rotate((int(R.random_int(0, width/3)) * 360) / width/70);
      translate(-d / 2, -d / 2);
let colors = deterministicShuffle(palette.concat(), R);
let unitFreq = R.random_num(0.01, 0.1);  // between 1% to 10% of width
