function getRandomColor(randomizer) {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[randomizer.random_int(0, 15)];
  }
  return color;
}

function deterministicShuffle(array, randomizer) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = randomizer.random_int(0, i);
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

