function draw() {
  background(0);
  let offset = width / 10;
  let p1 = createVector(offset, height / 2);
  let p2 = createVector(width - offset, height / 2);
  let brush_size = R.random_num(width/3, width/2);
  drawBrushLine(p1, p2, brush_size);
}

function drawBrushLine(p1, p2, brush_size) {

  let angle = atan2(p2.y - p1.y, p2.x - p1.x);
  let distance = p5.Vector.dist(p1, p2);
  push();
  translate(p1.x, p1.y);
  rotate(angle);
  let n = 0;
  while (n < distance) {
    push();
    translate(n, random(30));
    rotate(n/distance*random(5))
    let g = drawingContext.createLinearGradient(
      0,
      -brush_size / random(600),
      0,
      brush_size / random(30)
    );
    let m = 0;
    while (m < 1) {
      g.addColorStop(m, color(255,255/2*pow(noise(n / distance*random(10), m*random(100)), random(0,5))));
      m += noise(n / distance/random(2))/random(20);
    }
    drawingContext.strokeStyle = g;
    strokeWeight(random(1,3));
    line(0, -brush_size / 2, 0, brush_size / 2);
    pop();

    n++;
  }
  pop();
  noLoop();
}

let terrain;
let sizeA;
let tileSize = 4.5;
let scaleA;
let water = 0;
let sand = 0;
let grass = 0;
let stone = 0;
let snow = 0;

let randomNumber;

function setup() { 

  let R = new Random();
  randomNumber = R.random_int(0,100000000);

  let rsize = R.random_int(1,3);
  sizeA = getSize(rsize);
  rsize = R.random_int(1,3);
  scaleA = getScale(rsize);

  noLoop();
  randomSeed(randomNumber);
  //let rsize = random(0,3);
  //sizeA = getSize(rsize);
  //rsize = random(0,3);
  //scaleA = getScale(rsize);
  createCanvas(windowWidth, windowHeight,WEBGL);
  tileSize = windowWidth / 200;
  background(173, 216, 230);
  noiseSeed(randomNumber);
  terrain = generateHeightMap(sizeA, scaleA);

}

function draw() {
  orbitControl(1, 1, 0.1);
  translate(-sizeA * tileSize * 0.5, -sizeA * tileSize * 0.5, -100);
  drawTerrain(terrain, tileSize);
}

function generateHeightMap(sizeA, scaleA) {
  let heightMap = [];
  for (let x = 0; x < sizeA; x++) {
    heightMap[x] = [];
    for (let y = 0; y < sizeA; y++) {
      let elevation = noise(x * scaleA, y * scaleA);
      heightMap[x][y] = elevation;
    }
  }
  return heightMap;
}

function drawTerrain(heightMap, tileSize) {
  for (let x = 0; x < heightMap.length; x++) {
    for (let y = 0; y < heightMap[x].length; y++) {
      let elevation = heightMap[x][y];
      push();
      translate(x * tileSize, y * tileSize, elevation * tileSize * 50);

      if (elevation < 0.4) {
        drawWater(elevation);
      } else if (elevation < 0.5) {
        drawSand(elevation);
      } else if (elevation < 0.6) {
        drawGrass(elevation);
      } else if (elevation < 0.8) {
        drawStone(elevation);
      } else {
        drawSnow(elevation);
      }
      pop();
    }
  }
}

function drawWater(elevation) {
  water++;
  fill(0, 0, elevation*800);
  box(elevation*20, elevation*20, elevation*20);
}

function drawSand(elevation) {
  sand++;
  fill(elevation*500, elevation*500, elevation*250);
  box(elevation*20, elevation*20, elevation*20);
}

function drawSnow(elevation) {
  snow++;
  fill(elevation*255, elevation*255, elevation*255);
  box(elevation*20, elevation*20, elevation*20);
}

function drawGrass(elevation) {
  grass++;
  fill(0, elevation*200, 0);
  box(elevation*20, elevation*20, elevation*20);
}

function drawStone(elevation) {
  stone++;
  fill(elevation*130, elevation*130, elevation*130);
  box(elevation*20, elevation*20, elevation*20);
}

function getSize(rsize) {
  if (rsize <= 1) {
    return 50;
  } else if(rsize <= 2) {
    return 100;
  } else {
    return 200;
  }
}

function getScale(rsize) {
  if (rsize <= 1) {
    return 0.05;
  } else if(rsize <= 2) {
    return 0.1;
  } else {
    return 0.2;
  }
}


