let R;
let cwidth, cheight, margin, pointsx, ranges;
let moonX, moonY, moonSize, moonShades;
let stars = [];
let phase, rotationAngle;

function setup() {
	R = new Random();
	let seed = R.random_int(0, 99999);
	randomSeed(seed);
	noiseSeed(seed);

	cwidth = windowWidth;
	cheight = windowHeight;
	createCanvas(cwidth, cheight);
	margin = width/15;
	pointsx = Array.from({ length: (cwidth - margin * 2) });
	ranges = [
		[0, R.random_num(0.4, 0.6)],
		[0.2, R.random_num(0.5, 0.6)],
		[R.random_num(0.2, 0.3), 1],
	];
	let lowerBound = cheight / 3;
	moonX = R.random_num(margin, cwidth - margin);
	moonY = R.random_num(lowerBound, cheight - margin * R.random_num(3, 10));
	moonSize = R.random_num(width / 8, width / 4);
	moonShades = generateMoonShades(7);

	let daysSinceNewMoon = R.random_num(0, 29.53);
	phase = 2 * PI * daysSinceNewMoon / 29.53;
	rotationAngle = R.random_num(0, TWO_PI);

	for (let i = 0; i < R.random_int(100, 400); i++) {
		stars.push(createVector(R.random_num(0, cwidth), R.random_num(0, cheight * 0.66)));
	}

	background(0);
	ranges.forEach(drawRange);
	drawMoon();
	drawStars();
}

function drawRange([start, end], mindex) {
	let xoffset = margin + start * (cwidth - margin * 2);
	let rangeScale = end - start;
	for (let j = 0; j < R.random_num(1, 5); j++) {
		for (let i = 1; i < pointsx.length; i++) {
			drawLine(i, j, mindex, xoffset, rangeScale);
		}
	}
}

function drawLine(i, j, mindex, xoffset, rangeScale) {
	let ybaseline = cheight - margin;
	let pg = i / pointsx.length;
	let _pg = pg > 0.5 ? 1 - pg : pg;
	let scl = map(_pg, 0, 0.5, 0, 1);
	let mheight = width/1.5;
	let actualScale = scl * mheight;
	stroke("white");
	let sx = rangeScale * (i - 1);
	let sy = ybaseline - noise(sx / 90, j / 10, mindex * 500) * actualScale;
	let ex = rangeScale * i;
	let ey = ybaseline - noise(ex / 90, j / 10, mindex * 500) * actualScale;
	line(xoffset + sx, sy, xoffset + ex, ey);
}

function drawMoon() {
	push();
	translate(moonX, moonY);
	rotate(rotationAngle);
	noStroke();
	fill(255);
	ellipse(0, 0, moonSize);

	fill(0);
	let x = moonSize / 2 * cos(phase);
	ellipse(x, 0, moonSize);
	pop();
}

function generateMoonShades(numShades) {
	let shades = [];
	let baseColor = color(255, 255, 200);
	for (let i = 0; i < numShades; i++) {
		let shade = lerpColor(baseColor, color(255), i / (numShades - 1));
		shades.push(shade);
	}
	return shades;
}

function drawStars() {
	fill(255);
	noStroke();
	for (let star of stars) {
		ellipse(star.x, star.y, cwidth * 0.002);
	}
}

function windowResized() {
	resizeCanvas(windowWidth, windowHeight);
	setup();
}

