function unhexColor(color) {
  let a = color.substring(1, 3);
  let b = unhex(a);
  let result = {
    r: unhex(color.substring(1, 3)),
    g: unhex(color.substring(3, 5)),
    b: unhex(color.substring(5)),
  };
  return result;
}

function colorWithAlpha(color_struct, aAlpha) {
  let result =
    "rgba(" +
    color_struct.r +
    "," +
    color_struct.g +
    "," +
    color_struct.b +
    "," +
    aAlpha +
    ")";
  return result;
}

function setup() {

  createCanvas(window.innerWidth, window.innerHeight);
  g = createGraphics(coreSizeWidth, coreSizeHeight)
  g.background(250, 250, 250);
  noLoop();
  looping = false;

  localDraw();
}

function drawSection() {
  c = createGraphics(coreSizeWidth, coreSizeHeight)

  paletteColor = int(R.random_gaussian(9, 1));
  if (paletteColor > 13) paletteColor = 13;
  if (paletteColor < 5) paletteColor = 5;

  c.drawingContext.setLineDash([100, 20]);
  for (i = 0; i < R.random_gaussian(1000, 100); i++) {
    c.strokeWeight(R.random_gaussian(0.4, 2));
    c.stroke(
      colorWithAlpha(
        unhexColor(ALL_COLORS[paletteColor][int(R.random_num(0, 9))]),
        R.random_num(0, 10) / 10
      )
    );
    c.stroke(255, 255, 255);
    c.fill(
      colorWithAlpha(
        unhexColor(ALL_COLORS[paletteColor][int(R.random_num(0, 9))]),
        R.random_gaussian(1, 0.8)
      )
    );
    c.beginShape();
    c.vertex(
      R.random_gaussian(c.width / 2, c.width / 4),
      R.random_gaussian(c.height / 2, c.height / 4)
    );
    for (xx = 0; xx < 4; xx++)
      c.bezierVertex(
        R.random_gaussian(c.width / 2, c.width / 4),
        R.random_gaussian(c.height / 2, c.height / 4),
        R.random_gaussian(c.width / 2, c.width / 4),
        R.random_gaussian(c.height / 2, c.height / 4),
        R.random_gaussian(c.width / 2, c.width / 4),
        R.random_gaussian(c.height / 2, c.height / 4)
      );
    c.endShape();
  }
}

