    radiusX = R.random_num(base);
    radiusY = radiusX + R.random_gaussian(0, base / 10);
    g.ellipse(randomX, randomY + R.random_num(0, 100), radiusX, radiusY);

    radiusX = R.random_num(base);
    radiusY = radiusX + R.random_gaussian(0, base / 10);
    g.ellipse(randomX + R.random_num(0, 100), randomY + R.random_num(0, 100), radiusX, radiusY);
  }

  g.loadPixels();
  for (let i = 0; i < g.pixels.length; i++) {
    g.pixels[i] = g.pixels[i] + R.random_gaussian(0, 20);
  }
  g.updatePixels();
  if (threshold_yn) {
    g.filter(THRESHOLD);
  }

  g.image(sign, g.width - 160 + R.random_dec() * 10 - 5, g.height - 80 + R.random_dec() * 10 - 5);

  drawG();
}

function drawG() {
  clear();
  imageMode(CENTER);
  let pe = g.width / g.height;
  if (width >= height)
    image(g, width / 2, height / 2, pe * height, height);
  else
    image(g, width / 2, height / 2, width, 1 / pe * width);
}

function drawGlitch() {
  clear();
  background(g.get(g.width / 2, g.height / 2));
  imageMode(CENTER);
  let pe = glitch.width / glitch.height;
  if (width >= height)
    image(glitch, width / 2, height / 2, pe * height, height);
  else
    image(glitch, width / 2, height / 2, width, 1 / pe * width);

  var paletteColor = int(R.random_gaussian(2, 1));
  if (paletteColor < 0 || paletteColor > 13)
    paletteColor = 0;

  var lastX = 0;
  var lastY = 0;
  var cc;
  drawingContext.setLineDash([1, 5]);
  for (
    x = 0;
    x < window.innerWidth * 0.1;
    x = x + R.random_num(0, 10) // 20
  ) {
    for (
      y = 0;
      y < window.innerHeight * 0.1;
      y = y + R.random_num(0, 10) // 12
    ) {
      let variance = R.random_num(10, 30);
      let variance_x = 10 - R.random_num(0, 19);
      cc = colorWithAlpha(
        unhexColor(ALL_COLORS[paletteColor][int(R.random_num(0, 9))]),
        R.random_num(0, 10) / 10
      );
      stroke(cc);
      strokeWeight(0.4 + R.random_num(0, 10) / 3);
      var newX = 10 * x + 10 * variance_x;
      var newY = 10 * y + variance;
      line(lastX, lastY, newX, newY);
      lastX = newX;
      lastY = newY;
    }
  }
}

function draw() {
  if (looping) {
    // glitch animation
    if (frameCount % 6) {
      glitch = createGraphics(g.width, g.height);
      glitch.copy(g, 0, 0, g.width, g.height, 0, 0, g.width, g.height);
      for (var y = 0; y < glitch.height; y += R.random_int(10, 50)) {
        var deltaX = R.random_int(-150, 150);
        var deltaY = R.random_int(-150, 150);
        for (var x = 0; x < glitch.width; x += R.random_int(10, 50)) {
          glitch.stroke(g.get(x, y));

          glitch.fill(g.get(x, y));
          glitch.rect(x + deltaX, y + deltaY, R.random_int(30, 50), R.random_int(30, 50));
        }
      }
      drawGlitch();
    }
  }
}

function createCols(_url) {
  let slash_index = _url.lastIndexOf("/");
  let pallate_str = _url.slice(slash_index + 1);
  let arr = pallate_str.split("-");
  for (let i = 0; i < arr.length; i++) {
    arr[i] = "#" + arr[i];
  }
  return arr;
}

function keyPressed() {
  if (key == 's') {
    g.save("dont_hash_dash_" + tokenData.hash + ".png");
  } else if (key == 'l') {
    looping = !looping
    if (looping) {
      loop();
    }
    else {
      noLoop();
      drawG();
    }
  }
}

