let fc = 0;

function draw() {
  fc = frameCount;
  const circleSize = map(
    fc,
    0,
    endFrame + endSizeBase,
    size * 1.5,
    20,
    true
  );
  const cords = [];

  for (let i = 0; i < totalCords; i++) {
    const angle = map(i, 0, totalCords, 0, PI * 2);
    const r = circleSize / 2;
    cords.push([sin(angle) * r, cos(angle) * r]);
  }

  push();
  translate(size / 2, size / 2);
  rotate(initRotation);
  beginShape();

  cords.forEach(([x, y], index) => {
    rotate(fc / width / 3);
    const selectedPalette = R.random_choice(colors);
    const numColorsInPalette = selectedPalette.length;
    const selectedColor = selectedPalette[index % numColorsInPalette];
    fill(selectedColor);
    noStroke();

    const [noiseStart, noiseEnd] = noisePattern;
    const noisiness = map(fc, 0, endFrame, noiseStart, noiseEnd);
    const xDelta = (noise(index / 100, fc / 30, x / 100) - 0.5) * noisiness;
    const yDelta = (noise(index / 100, fc / 30, y / 100) - 0.5) * noisiness;
    const _x = x + xDelta;
    const _y = y + yDelta;
    curveVertex(_x, _y);

    push();
    fill("black");
    noStroke();
    let dotSize = noise(fc / width / 3) * 7;
    ellipse(x, y, dotSize);
    pop();
  });

  endShape(CLOSE);
  pop();

  if (fc > endFrame) {
    noLoop();
  }
}

