function divideRect(x, y, s) {
  fill(R.random_choice(colors));
  noStroke();
  push();
    translate(x, y);
    rect(0, 0, s, s);
    rotate(int(R.random_int(0, 3)) / 4 * 2 * PI);
    translate(s / 2, s / 2);
    fill(R.random_choice(colors));
    
    if (R.random_bool(0.5) && seed > 0.2) {
      arc(0, 0, s / 2, s / 2, 0, 1.5 * PI);
    } else if (R.random_bool(0.9) && seed > 0.1) {
      triangle(0, 0, s / 2, s / 2, s / 2, 0);
    } else if (R.random_bool(0.3)) {
      ellipse(0, 0, s / 2, s / 2);
    }
  pop();
  
  if (R.random_bool(0.3 + map(sqrt(s), 30, 0, 1, 0, true)) && s > 10) {
    setTimeout(() => {
      divideRect(x, y, s / 2);
      divideRect(x + s / 2, y, s / 2);
      divideRect(x, y + s / 2, s / 2);
      divideRect(x + s / 2, y + s / 2, s / 2);
    }, 150);
  }
}

