  rotateY(frameCount * 0.01);
  rotateX(frameCount * 0.01);

  for (let i = 1; i < R.random_int(5,50); i++) {
    drawSineWave(i);
    rotateZ(R.random_dec(0,2));
  }
}

function drawSineWave(modifier) {
  const center = createVector(0, 0);

  for (let i = 0; i <= 360; i++) {
    const angle = radians(i);
    const radius = (height / 2) * (i / 360);
    const rotation = radians(radius * 10) + R.random_dec(-0.5, 0.5);

    const position = createVector(
      center.x + radius * cos(angle + rotation),
      center.y + radius * sin(angle + rotation),
      radius / 3 // scale in z-direction
    );

    let amplitude = (i / 10) * (modifier / 20) + R.random_dec(-2, 5);
    const maxAmplitude = radius / 5;
    amplitude = min(amplitude, maxAmplitude);

    const offset = createVector(modifier, 0, 0);
    const wavePosition = p5.Vector.add(position, offset);

    strokeWeight(R.random_dec(0.1, 2.1));
    const hue = (i / 360) * 360;
    const saturation = map(modifier, 1, 50, 30, 100);
    const brightness = map(position.y, -height / 2, height / 2, 50, 100);
    stroke(`hsl(${hue}, ${saturation}%, ${brightness}%)`);

    if (i > 0) {
      curveTightness(R.random_dec(0.1, 0.9));
      curve(
        lastWavePosition.x,
        lastWavePosition.y,
        lastWavePosition.z,
        wavePosition.x,
        wavePosition.y,
        wavePosition.z,
        i,
        i
      );
      
      // Add a sphere at the wavePosition
      push();
      translate(wavePosition.x, wavePosition.y, wavePosition.z);
      fill(sphereColor);
      sphere(sphereSize);
      pop();
    }
    rotateZ(i);

    lastWavePosition = wavePosition;
  }
}

