function setup() {
	orientation = "Portrait";
	if (orientation==="Portrait"){
		createCanvas(.80 * windowHeight, windowHeight);
	} else if (orientation==="Landscape"){
		let dimen = min(windowHeight, windowWidth);
		createCanvas(dimen, dimen);
	}
	
	pixelDensity(2);
	acurrentPalette = currentPalette.slice(0,bckID).concat(currentPalette.slice(bckID+1));
	colorMCode = R.random_int(3,acurrentPalette.length-2);
	conSlice = R.random_int(0,acurrentPalette.length-1-colorMCode);
	acurrentPalette = acurrentPalette.slice(conSlice,conSlice+colorMCode);
	prepper();
	noLoop();
}

function prepper() {
	fSc = orientation==="Portrait" ? width / 603: height / 603, background(bckColor);
	initialRadius = orientation==="Portrait" ? (R.random_dec() * (3 * width / 8)) + (width / 8): (R.random_dec() * (3 * height / 8)) + (height / 8), 
	marg = orientation==="Portrait" ? width/5: height/5, 
	initialX = R.random_num(marg, width-marg), 
	initialY = R.random_num(marg, height-marg);
    initialRadius = (rGirth * fSc);
	const e = new Circle(initialX, initialY, width/8);
    circles.push(e);
    for (let e = 0; e < totalCircles - 1; e++) packCircle();
    points = circles.map(e => [e.x, e.y]), triangles = Delaunay.triangulate(points)
}

function draw() {
    noFill();

    for (let e = 0, cccol; e < triangles.length; e += 3) {
		cccol = R.random_choice(acurrentPalette);
		if (R.random_dec()<0.55){
			noFill(), stroke(cccol + "ff"), strokeWeight((rGirth+2) * fSc);
			const t = points[triangles[e]],
				i = points[triangles[e + 1]],
				r = points[triangles[e + 2]];
			if (R.random_dec()<1){
				stroke("#000000" + "66");
				line(t[0]+(2* fSc), t[1]+(1* fSc), i[0], i[1]);
				fill(cccol + "ff");
				noStroke(), noFill();
				ellipse(t[0], t[1], (rGirth) * fSc);
				ellipse(i[0], i[1], (rGirth) * fSc);
				
				noFill(), stroke(R.random_choice(acurrentPalette) + "ff");
				strokeWeight((rGirth+2) * fSc);
				line(t[0], t[1], i[0], i[1]);
				
				strokeWeight(1 * fSc);
				line(t[0], t[1], i[0], i[1]);
			} else {
				stroke("#000000" + "66");
				line(t[0]+(1* fSc), t[1]+(1* fSc), r[0], r[1]);
				fill(cccol + "ff");
				noStroke(), noFill();
				ellipse(t[0], t[1], (rGirth) * fSc);
				ellipse(r[0], r[1], (rGirth) * fSc);
				
				noFill(), stroke(R.random_choice(acurrentPalette) + "ff");
				strokeWeight((rGirth+2) * fSc);
				line(t[0], t[1], r[0], r[1]);
				
				strokeWeight(1 * fSc);
				line(t[0], t[1], r[0], r[1]);
			}
		}
    }
	
	for (let e = 0; e < triangles.length; e += 3) {
		if (R.random_dec()<0.15){
			noFill(), stroke(R.random_choice(acurrentPalette) + "66"), strokeWeight((rGirth/15) * fSc);
			const t = points[triangles[e]],
				i = points[triangles[e + 1]],
				r = points[triangles[e + 2]];
		}
    }

	drawingContext.setLineDash([0,0]);
	strokeWeight(R.random_num(1,5) * fSc);
	for (let e = 0; e < circles.length; e++){
		if (R.random_dec()<0.6){
			circles[e].draw2();	
			circles[e].draw();
		}		
	}
	
	drawingContext.setLineDash([0,0]);
	for (let e = 0; e < circles.length; e++){
		if (R.random_dec()<1){
			strokeWeight(0.3 * fSc);
			circles[e].draw3();
		}
	}

	for (let e = 0; e < triangles.length; e += 3) {
		if (R.random_dec()<0.5){
			noFill(), stroke(R.random_choice(acurrentPalette) + "66"), strokeWeight(0.8 * fSc);
			const t = points[triangles[e]],
				i = points[triangles[e + 1]],
				r = points[triangles[e + 2]];
			
			triangle(t[0], t[1], r[0], r[1], r[0], r[1]);
		}
    }

